INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5672,78,'An-Naba`',1,"عَمَّ يَتَسَاۤءَلُوْنَۚ", "Tentang apakah mereka saling bertanya-tanya?", "amma yatasa alụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5673,78,'An-Naba`',2,"عَنِ النَّبَاِ الْعَظِيْمِۙ", "Tentang berita yang besar (hari kebangkitan),", "'anin-naba`il-'aẓīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5674,78,'An-Naba`',3,"الَّذِيْ هُمْ فِيْهِ مُخْتَلِفُوْنَۗ", "yang dalam hal itu mereka berselisih.", "allażī hum fīhi mukhtalifụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5675,78,'An-Naba`',4,"كَلَّا سَيَعْلَمُوْنَۙ", "Tidak! Kelak mereka akan mengetahui,", "kallā saya'lamụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5676,78,'An-Naba`',5,"ثُمَّ كَلَّا سَيَعْلَمُوْنَ", "sekali lagi tidak! Kelak mereka akan mengetahui.", "ṡumma kallā saya'lamụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5677,78,'An-Naba`',6,"اَلَمْ نَجْعَلِ الْاَرْضَ مِهٰدًاۙ", "Bukankah Kami telah menjadikan bumi sebagai hamparan,", "a lam naj'alil-arḍa mihādā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5678,78,'An-Naba`',7,"وَّالْجِبَالَ اَوْتَادًاۖ", "dan gunung-gunung sebagai pasak?", "wal-jibāla autādā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5679,78,'An-Naba`',8,"وَّخَلَقْنٰكُمْ اَزْوَاجًاۙ", "Dan Kami menciptakan kamu berpasang-pasangan,", "wa khalaqnākum azwājā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5680,78,'An-Naba`',9,"وَّجَعَلْنَا نَوْمَكُمْ سُبَاتًاۙ", "dan Kami menjadikan tidurmu untuk istirahat,", "wa ja'alnā naumakum subātā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5681,78,'An-Naba`',10,"وَّجَعَلْنَا الَّيْلَ لِبَاسًاۙ", "dan Kami menjadikan malam sebagai pakaian,", "wa ja'alnal-laila libāsā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5682,78,'An-Naba`',11,"وَّجَعَلْنَا النَّهَارَ مَعَاشًاۚ", "dan Kami menjadikan siang untuk mencari penghidupan,", "wa ja'alnan-nahāra ma'āsyā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5683,78,'An-Naba`',12,"وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًاۙ", "dan Kami membangun di atas kamu tujuh (langit) yang kokoh,", "wa banainā fauqakum sab'an syidādā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5684,78,'An-Naba`',13,"وَّجَعَلْنَا سِرَاجًا وَّهَّاجًاۖ", "dan Kami menjadikan pelita yang terang-benderang (matahari),", "wa ja'alnā sirājaw wahhājā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5685,78,'An-Naba`',14,"وَّاَنْزَلْنَا مِنَ الْمُعْصِرٰتِ مَاۤءً ثَجَّاجًاۙ", "dan Kami turunkan dari awan, air hujan yang tercurah dengan hebatnya,", "wa anzalnā minal-mu'ṣirāti mā`an ṡajjājā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5686,78,'An-Naba`',15,"لِّنُخْرِجَ بِهٖ حَبًّا وَّنَبَاتًاۙ", "untuk Kami tumbuhkan dengan air itu biji-bijian dan tanam-tanaman,", "linukhrija bihī ḥabbaw wa nabātā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5687,78,'An-Naba`',16,"وَّجَنّٰتٍ اَلْفَافًاۗ", "dan kebun-kebun yang rindang.", "wa jannātin alfāfā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5688,78,'An-Naba`',17,"اِنَّ يَوْمَ الْفَصْلِ كَانَ مِيْقَاتًاۙ", "Sungguh, hari keputusan adalah suatu waktu yang telah ditetapkan,", "inna yaumal-faṣli kāna mīqātā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5689,78,'An-Naba`',18,"يَّوْمَ يُنْفَخُ فِى الصُّوْرِ فَتَأْتُوْنَ اَفْوَاجًاۙ", "(yaitu) pada hari (ketika) sangkakala ditiup, lalu kamu datang berbondong-bondong,", "yauma yunfakhu fiṣ-ṣụri fa ta`tụna afwājā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5690,78,'An-Naba`',19,"وَّفُتِحَتِ السَّمَاۤءُ فَكَانَتْ اَبْوَابًاۙ", "dan langit pun dibukalah, maka terdapatlah beberapa pintu,", "wa futiḥatis-samā`u fa kānat abwābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5691,78,'An-Naba`',20,"وَّسُيِّرَتِ الْجِبَالُ فَكَانَتْ سَرَابًاۗ", "dan gunung-gunung pun dijalankan sehingga menjadi fatamorgana.", "wa suyyiratil-jibālu fa kānat sarābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5692,78,'An-Naba`',21,"اِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًاۙ", "Sungguh, (neraka) Jahanam itu (sebagai) tempat mengintai (bagi penjaga yang mengawasi isi neraka),", "inna jahannama kānat mirṣādā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5693,78,'An-Naba`',22,"لِّلطّٰغِيْنَ مَاٰبًاۙ", "menjadi tempat kembali bagi orang-orang yang melampaui batas.", "liṭ-ṭāgīna ma`ābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5694,78,'An-Naba`',23,"لّٰبِثِيْنَ فِيْهَآ اَحْقَابًاۚ", "Mereka tinggal di sana dalam masa yang lama,", "lābiṡīna fīhā aḥqābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5695,78,'An-Naba`',24,"لَا يَذُوْقُوْنَ فِيْهَا بَرْدًا وَّلَا شَرَابًاۙ", "mereka tidak merasakan kesejukan di dalamnya dan tidak (pula mendapat) minuman,", "lā yażụqụna fīhā bardaw wa lā syarābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5696,78,'An-Naba`',25,"اِلَّا حَمِيْمًا وَّغَسَّاقًاۙ", "selain air yang mendidih dan nanah,", "illā ḥamīmaw wa gassāqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5697,78,'An-Naba`',26,"جَزَاۤءً وِّفَاقًاۗ", "sebagai pembalasan yang setimpal.", "jazā`aw wifāqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5698,78,'An-Naba`',27,"اِنَّهُمْ كَانُوْا لَا يَرْجُوْنَ حِسَابًاۙ", "Sesungguhnya dahulu mereka tidak pernah mengharapkan perhitungan.", "innahum kānụ lā yarjụna ḥisābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5699,78,'An-Naba`',28,"وَّكَذَّبُوْا بِاٰيٰتِنَا كِذَّابًاۗ", "Dan mereka benar-benar mendustakan ayat-ayat Kami.", "wa każżabụ bi`āyātinā kiżżābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5700,78,'An-Naba`',29,"وَكُلَّ شَيْءٍ اَحْصَيْنٰهُ كِتٰبًاۙ", "Dan segala sesuatu telah Kami catat dalam suatu Kitab (buku catatan amalan manusia).", "wa kulla syai`in aḥṣaināhu kitābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5701,78,'An-Naba`',30,"فَذُوْقُوْا فَلَنْ نَّزِيْدَكُمْ اِلَّا عَذَابًا", "Maka karena itu rasakanlah! Maka tidak ada yang akan Kami tambahkan kepadamu selain azab.", "fa żụqụ fa lan nazīdakum illā 'ażābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5702,78,'An-Naba`',31,"اِنَّ لِلْمُتَّقِيْنَ مَفَازًاۙ", "Sungguh, orang-orang yang bertakwa mendapat kemenangan,", "inna lil-muttaqīna mafāzā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5703,78,'An-Naba`',32,"حَدَاۤىِٕقَ وَاَعْنَابًاۙ", "(yaitu) kebun-kebun dan buah anggur,", "ḥadā`iqa wa a'nābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5704,78,'An-Naba`',33,"وَّكَوَاعِبَ اَتْرَابًاۙ", "dan gadis-gadis montok yang sebaya,", "wa kawā'iba atrābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5705,78,'An-Naba`',34,"وَّكَأْسًا دِهَاقًاۗ", "dan gelas-gelas yang penuh (berisi minuman).", "wa ka`san dihāqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5706,78,'An-Naba`',35,"لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا وَّلَا كِذَّابًا", "Di sana mereka tidak mendengar percakapan yang sia-sia maupun (perkataan) dusta.", "lā yasma'ụna fīhā lagwaw wa lā kiżżābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5707,78,'An-Naba`',36,"جَزَاۤءً مِّنْ رَّبِّكَ عَطَاۤءً حِسَابًاۙ", "Sebagai balasan dan pemberian yang cukup banyak dari Tuhanmu,", "jazā`am mir rabbika 'aṭā`an ḥisābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5708,78,'An-Naba`',37,"رَّبِّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا الرَّحْمٰنِ لَا يَمْلِكُوْنَ مِنْهُ خِطَابًاۚ", "Tuhan (yang memelihara) langit dan bumi dan apa yang ada di antara keduanya; Yang Maha Pengasih, mereka tidak mampu berbicara dengan Dia.", "rabbis-samāwāti wal-arḍi wa mā bainahumar-raḥmāni lā yamlikụna min-hu khiṭābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5709,78,'An-Naba`',38,"يَوْمَ يَقُوْمُ الرُّوْحُ وَالْمَلٰۤىِٕكَةُ صَفًّاۙ لَّا يَتَكَلَّمُوْنَ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَقَالَ صَوَابًا", "Pada hari, ketika ruh dan para malaikat berdiri bersaf-saf, mereka tidak berkata-kata, kecuali siapa yang telah diberi izin kepadanya oleh Tuhan Yang Maha Pengasih dan dia hanya mengatakan yang benar.", "yauma yaqụmur-rụḥu wal-malā`ikatu ṣaffal lā yatakallamụna illā man ażina lahur-raḥmānu wa qāla ṣawābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5710,78,'An-Naba`',39,"ذٰلِكَ الْيَوْمُ الْحَقُّۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ مَاٰبًا", "Itulah hari yang pasti terjadi. Maka barang siapa menghendaki, niscaya dia menempuh jalan kembali kepada Tuhannya.", "żālikal-yaumul-ḥaqq, fa man syā`attakhaża ilā rabbihī ma`ābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5711,78,'An-Naba`',40,"اِنَّآ اَنْذَرْنٰكُمْ عَذَابًا قَرِيْبًا ەۙ يَّوْمَ يَنْظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُوْلُ الْكٰفِرُ يٰلَيْتَنِيْ كُنْتُ تُرَابًا", "Sesungguhnya Kami telah memperingatkan kepadamu (orang kafir) azab yang dekat, pada hari manusia melihat apa yang telah diperbuat oleh kedua tangannya; dan orang kafir berkata, “Alangkah baiknya seandainya dahulu aku jadi tanah.”", "innā anżarnākum 'ażābang qarībay yauma yanẓurul-mar`u mā qaddamat yadāhu wa yaqụlul-kāfiru yā laitanī kuntu turābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5712,79,'An-Nazi`at',1,"وَالنّٰزِعٰتِ غَرْقًاۙ", "Demi (malaikat) yang mencabut (nyawa) dengan keras.", "wan-nāzi'āti garqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5713,79,'An-Nazi`at',2,"وَّالنّٰشِطٰتِ نَشْطًاۙ", "Demi (malaikat) yang mencabut (nyawa) dengan lemah lembut.", "wan-nāsyiṭāti nasyṭā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5714,79,'An-Nazi`at',3,"وَّالسّٰبِحٰتِ سَبْحًاۙ", "Demi (malaikat) yang turun dari langit dengan cepat,", "was-sābiḥāti sab-ḥā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5715,79,'An-Nazi`at',4,"فَالسّٰبِقٰتِ سَبْقًاۙ", "dan (malaikat) yang mendahului dengan kencang,", "fas-sābiqāti sabqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5716,79,'An-Nazi`at',5,"فَالْمُدَبِّرٰتِ اَمْرًاۘ", "dan (malaikat) yang mengatur urusan (dunia).", "fal-mudabbirāti amrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5717,79,'An-Nazi`at',6,"يَوْمَ تَرْجُفُ الرَّاجِفَةُۙ", "(Sungguh, kamu akan dibangkitkan) pada hari ketika tiupan pertama mengguncangkan alam,", "yauma tarjufur-rājifah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5718,79,'An-Nazi`at',7,"تَتْبَعُهَا الرَّادِفَةُ ۗ", "(tiupan pertama) itu diiringi oleh tiupan kedua.", "tatba'uhar-rādifah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5719,79,'An-Nazi`at',8,"قُلُوْبٌ يَّوْمَىِٕذٍ وَّاجِفَةٌۙ", "Hati manusia pada waktu itu merasa sangat takut,", "qulụbuy yauma`iżiw wājifah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5720,79,'An-Nazi`at',9,"اَبْصَارُهَا خَاشِعَةٌ ۘ", "pandangannya tunduk.", "abṣāruhā khāsyi'ah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5721,79,'An-Nazi`at',10,"يَقُوْلُوْنَ ءَاِنَّا لَمَرْدُوْدُوْنَ فِى الْحَافِرَةِۗ", "(Orang-orang kafir) berkata, “Apakah kita benar-benar akan dikembalikan kepada kehidupan yang semula?", "yaqụlụna a innā lamardụdụna fil-ḥāfirah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5722,79,'An-Nazi`at',11,"ءَاِذَا كُنَّا عِظَامًا نَّخِرَةً ۗ", "Apakah (akan dibangkitkan juga) apabila kita telah menjadi tulang belulang yang hancur?”", "a iżā kunnā 'iẓāman nakhirah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5723,79,'An-Nazi`at',12,"قَالُوْا تِلْكَ اِذًا كَرَّةٌ خَاسِرَةٌ ۘ", "Mereka berkata, “Kalau demikian, itu adalah suatu pengembalian yang merugikan.”", "qālụ tilka iżang karratun khāsirah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5724,79,'An-Nazi`at',13,"فَاِنَّمَا هِيَ زَجْرَةٌ وَّاحِدَةٌۙ", "Maka pengembalian itu hanyalah dengan sekali tiupan saja.", "fa innamā hiya zajratuw wāḥidah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5725,79,'An-Nazi`at',14,"فَاِذَا هُمْ بِالسَّاهِرَةِۗ", "Maka seketika itu mereka hidup kembali di bumi (yang baru).", "fa iżā hum bis-sāhirah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5726,79,'An-Nazi`at',15,"هَلْ اَتٰىكَ حَدِيْثُ مُوْسٰىۘ", "Sudahkah sampai kepadamu (Muhammad) kisah Musa?", "hal atāka ḥadīṡu mụsā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5727,79,'An-Nazi`at',16,"اِذْ نَادٰىهُ رَبُّهٗ بِالْوَادِ الْمُقَدَّسِ طُوًىۚ", "Ketika Tuhan memanggilnya (Musa) di lembah suci yaitu Lembah Tuwa;", "iż nādāhu rabbuhụ bil-wādil-muqaddasi ṭuwā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5728,79,'An-Nazi`at',17,"اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰىۖ", "pergilah engkau kepada Fir‘aun! Sesungguhnya dia telah melampaui batas,", "iż-hab ilā fir'auna innahụ ṭagā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5729,79,'An-Nazi`at',18,"فَقُلْ هَلْ لَّكَ اِلٰٓى اَنْ تَزَكّٰىۙ", "Maka katakanlah (kepada Fir‘aun), “Adakah keinginanmu untuk membersihkan diri (dari kesesatan),", "fa qul hal laka ilā an tazakkā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5730,79,'An-Nazi`at',19,"وَاَهْدِيَكَ اِلٰى رَبِّكَ فَتَخْشٰىۚ", "dan engkau akan kupimpin ke jalan Tuhanmu agar engkau takut kepada-Nya?”", "wa ahdiyaka ilā rabbika fa takhsyā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5731,79,'An-Nazi`at',20,"فَاَرٰىهُ الْاٰيَةَ الْكُبْرٰىۖ", "Lalu (Musa) memperlihatkan kepadanya mukjizat yang besar.", "fa arāhul-āyatal-kubrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5732,79,'An-Nazi`at',21,"فَكَذَّبَ وَعَصٰىۖ", "Tetapi dia (Fir‘aun) mendustakan dan mendurhakai.", "fa każżaba wa 'aṣā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5733,79,'An-Nazi`at',22,"ثُمَّ اَدْبَرَ يَسْعٰىۖ", "Kemudian dia berpaling seraya berusaha menantang (Musa).", "ṡumma adbara yas'ā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5734,79,'An-Nazi`at',23,"فَحَشَرَ فَنَادٰىۖ", "Kemudian dia mengumpulkan (pembesar-pembesarnya) lalu berseru (memanggil kaumnya).", "fa ḥasyara fa nādā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5735,79,'An-Nazi`at',24,"فَقَالَ اَنَا۠ رَبُّكُمُ الْاَعْلٰىۖ", "(Seraya) berkata, “Akulah tuhanmu yang paling tinggi.”", "fa qāla ana rabbukumul-a'lā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5736,79,'An-Nazi`at',25,"فَاَخَذَهُ اللّٰهُ نَكَالَ الْاٰخِرَةِ وَالْاُوْلٰىۗ", "Maka Allah menghukumnya dengan azab di akhirat dan siksaan di dunia.", "fa akhażahullāhu nakālal-ākhirati wal-ụlā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5737,79,'An-Nazi`at',26,"اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّمَنْ يَّخْشٰى ۗ", "Sungguh, pada yang demikian itu terdapat pelajaran bagi orang yang takut (kepada Allah).", "inna fī żālika la'ibratal limay yakhsyā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5738,79,'An-Nazi`at',27,"ءَاَنْتُمْ اَشَدُّ خَلْقًا اَمِ السَّمَاۤءُ ۚ بَنٰىهَاۗ", "Apakah penciptaan kamu yang lebih hebat ataukah langit yang telah dibangun-Nya?", "a antum asyaddu khalqan amis-samā`, banāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5739,79,'An-Nazi`at',28,"رَفَعَ سَمْكَهَا فَسَوّٰىهَاۙ", "Dia telah meninggikan bangunannya lalu menyempurnakannya,", "rafa'a samkahā fa sawwāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5740,79,'An-Nazi`at',29,"وَاَغْطَشَ لَيْلَهَا وَاَخْرَجَ ضُحٰىهَاۖ", "dan Dia menjadikan malamnya (gelap gulita), dan menjadikan siangnya (terang benderang).", "wa agṭasya lailahā wa akhraja ḍuḥāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5741,79,'An-Nazi`at',30,"وَالْاَرْضَ بَعْدَ ذٰلِكَ دَحٰىهَاۗ", "Dan setelah itu bumi Dia hamparkan.", "wal-arḍa ba'da żālika daḥāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5742,79,'An-Nazi`at',31,"اَخْرَجَ مِنْهَا مَاۤءَهَا وَمَرْعٰىهَاۖ", "Darinya Dia pancarkan mata air, dan (ditumbuhkan) tumbuh-tumbuhannya.", "akhraja min-hā mā`ahā wa mar'āhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5743,79,'An-Nazi`at',32,"وَالْجِبَالَ اَرْسٰىهَاۙ", "Dan gunung-gunung Dia pancangkan dengan teguh.", "wal-jibāla arsāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5744,79,'An-Nazi`at',33,"مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ", "(Semua itu) untuk kesenanganmu dan untuk hewan-hewan ternakmu.", "matā'al lakum wa li`an'āmikum");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5745,79,'An-Nazi`at',34,"فَاِذَا جَاۤءَتِ الطَّاۤمَّةُ الْكُبْرٰىۖ", "Maka apabila malapetaka besar (hari Kiamat) telah datang,", "fa iżā jā`atiṭ-ṭāmmatul-kubrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5746,79,'An-Nazi`at',35,"يَوْمَ يَتَذَكَّرُ الْاِنْسَانُ مَا سَعٰىۙ", "yaitu pada hari (ketika) manusia teringat akan apa yang telah dikerjakannya,", "yauma yatażakkarul-insānu mā sa'ā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5747,79,'An-Nazi`at',36,"وَبُرِّزَتِ الْجَحِيْمُ لِمَنْ يَّرٰى", "dan neraka diperlihatkan dengan jelas kepada setiap orang yang melihat.", "wa burrizatil-jaḥīmu limay yarā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5748,79,'An-Nazi`at',37,"فَاَمَّا مَنْ طَغٰىۖ", "Maka adapun orang yang melampaui batas,", "fa ammā man ṭagā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5749,79,'An-Nazi`at',38,"وَاٰثَرَ الْحَيٰوةَ الدُّنْيَاۙ", "dan lebih mengutamakan kehidupan dunia,", "wa āṡaral-ḥayātad-dun-yā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5750,79,'An-Nazi`at',39,"فَاِنَّ الْجَحِيْمَ هِيَ الْمَأْوٰىۗ", "maka sungguh, nerakalah tempat tinggalnya.", "fa innal-jaḥīma hiyal-ma`wā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5751,79,'An-Nazi`at',40,"وَاَمَّا مَنْ خَافَ مَقَامَ رَبِّهٖ وَنَهَى النَّفْسَ عَنِ الْهَوٰىۙ", "Dan adapun orang-orang yang takut kepada kebesaran Tuhannya dan menahan diri dari (keinginan) hawa nafsunya,", "wa ammā man khāfa maqāma rabbihī wa nahan-nafsa 'anil-hawā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5752,79,'An-Nazi`at',41,"فَاِنَّ الْجَنَّةَ هِيَ الْمَأْوٰىۗ", "maka sungguh, surgalah tempat tinggal(nya).", "fa innal-jannata hiyal-ma`wā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5753,79,'An-Nazi`at',42,"يَسْـَٔلُوْنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰىهَاۗ", "Mereka (orang-orang kafir) bertanya kepadamu (Muhammad) tentang hari Kiamat, “Kapankah terjadinya?”", "yas`alụnaka 'anis-sā'ati ayyāna mursāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5754,79,'An-Nazi`at',43,"فِيْمَ اَنْتَ مِنْ ذِكْرٰىهَاۗ", "Untuk apa engkau perlu menyebutkannya (waktunya)?", "fīma anta min żikrāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5755,79,'An-Nazi`at',44,"اِلٰى رَبِّكَ مُنْتَهٰىهَاۗ", "Kepada Tuhanmulah (dikembalikan) kesudahannya (ketentuan waktunya).", "ilā rabbika muntahāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5756,79,'An-Nazi`at',45,"اِنَّمَآ اَنْتَ مُنْذِرُ مَنْ يَّخْشٰىهَاۗ", "Engkau (Muhammad) hanyalah pemberi peringatan bagi siapa yang takut kepadanya (hari Kiamat).", "innamā anta munżiru may yakhsyāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5757,79,'An-Nazi`at',46,"كَاَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوْٓا اِلَّا عَشِيَّةً اَوْ ضُحٰىهَا", "Pada hari ketika mereka melihat hari Kiamat itu (karena suasananya hebat), mereka merasa seakan-akan hanya (sebentar saja) tinggal (di dunia) pada waktu sore atau pagi hari.", "ka`annahum yauma yaraunahā lam yalbaṡū illā 'asyiyyatan au ḍuḥāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5758,80,'`Abasa',1,"عَبَسَ وَتَوَلّٰىٓۙ", "Dia (Muhammad) berwajah masam dan berpaling,", "'abasa wa tawallā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5759,80,'`Abasa',2,"اَنْ جَاۤءَهُ الْاَعْمٰىۗ", "karena seorang buta telah datang kepadanya (Abdullah bin Ummi Maktum).", "an jā`ahul-a'mā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5760,80,'`Abasa',3,"وَمَا يُدْرِيْكَ لَعَلَّهٗ يَزَّكّٰىٓۙ", "Dan tahukah engkau (Muhammad) barangkali dia ingin menyucikan dirinya (dari dosa),", "wa mā yudrīka la'allahụ yazzakkā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5761,80,'`Abasa',4,"اَوْ يَذَّكَّرُ فَتَنْفَعَهُ الذِّكْرٰىۗ", "atau dia (ingin) mendapatkan pengajaran, yang memberi manfaat kepadanya?", "au yażżakkaru fa tanfa'ahuż-żikrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5762,80,'`Abasa',5,"اَمَّا مَنِ اسْتَغْنٰىۙ", "Adapun orang yang merasa dirinya serba cukup (pembesar-pembesar Quraisy),", "ammā manistagnā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5763,80,'`Abasa',6,"فَاَنْتَ لَهٗ تَصَدّٰىۗ", "maka engkau (Muhammad) memberi perhatian kepadanya,", "fa anta lahụ taṣaddā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5764,80,'`Abasa',7,"وَمَا عَلَيْكَ اَلَّا يَزَّكّٰىۗ", "padahal tidak ada (cela) atasmu kalau dia tidak menyucikan diri (beriman).", "wa mā 'alaika allā yazzakkā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5765,80,'`Abasa',8,"وَاَمَّا مَنْ جَاۤءَكَ يَسْعٰىۙ", "Dan adapun orang yang datang kepadamu dengan bersegera (untuk mendapatkan pengajaran),", "wa ammā man jā`aka yas'ā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5766,80,'`Abasa',9,"وَهُوَ يَخْشٰىۙ", "sedang dia takut (kepada Allah),", "wa huwa yakhsyā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5767,80,'`Abasa',10,"فَاَنْتَ عَنْهُ تَلَهّٰىۚ", "engkau (Muhammad) malah mengabaikannya.", "fa anta 'an-hu talahhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5768,80,'`Abasa',11,"كَلَّآ اِنَّهَا تَذْكِرَةٌ ۚ", "Sekali-kali jangan (begitu)! Sungguh, (ajaran-ajaran Allah) itu suatu peringatan,", "kallā innahā tażkirah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5769,80,'`Abasa',12,"فَمَنْ شَاۤءَ ذَكَرَهٗ ۘ", "maka barangsiapa menghendaki, tentulah dia akan memperhatikannya,", "fa man syā`a żakarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5770,80,'`Abasa',13,"فِيْ صُحُفٍ مُّكَرَّمَةٍۙ", "di dalam kitab-kitab yang dimuliakan (di sisi Allah),", "fī ṣuḥufim mukarramah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5771,80,'`Abasa',14,"مَّرْفُوْعَةٍ مُّطَهَّرَةٍ ۢ ۙ", "yang ditinggikan (dan) disucikan,", "marfụ'atim muṭahharah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5772,80,'`Abasa',15,"بِاَيْدِيْ سَفَرَةٍۙ", "di tangan para utusan (malaikat),", "bi`aidī safarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5773,80,'`Abasa',16,"كِرَامٍۢ بَرَرَةٍۗ", "yang mulia lagi berbakti.", "kirāmim bararah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5774,80,'`Abasa',17,"قُتِلَ الْاِنْسَانُ مَآ اَكْفَرَهٗۗ", "Celakalah manusia! Alangkah kufurnya dia!", "qutilal-insānu mā akfarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5775,80,'`Abasa',18,"مِنْ اَيِّ شَيْءٍ خَلَقَهٗۗ", "Dari apakah Dia (Allah) menciptakannya?", "min ayyi syai`in khalaqah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5776,80,'`Abasa',19,"مِنْ نُّطْفَةٍۗ خَلَقَهٗ فَقَدَّرَهٗۗ", "Dari setetes mani, Dia menciptakannya lalu menentukannya.", "min nuṭfah, khalaqahụ fa qaddarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5777,80,'`Abasa',20,"ثُمَّ السَّبِيْلَ يَسَّرَهٗۙ", "Kemudian jalannya Dia mudahkan,", "ṡummas-sabīla yassarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5778,80,'`Abasa',21,"ثُمَّ اَمَاتَهٗ فَاَقْبَرَهٗۙ", "kemudian Dia mematikannya lalu menguburkannya,", "ṡumma amātahụ fa aqbarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5779,80,'`Abasa',22,"ثُمَّ اِذَا شَاۤءَ اَنْشَرَهٗۗ", "kemudian jika Dia menghendaki, Dia membangkitkannya kembali.", "ṡumma iżā syā`a ansyarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5780,80,'`Abasa',23,"كَلَّا لَمَّا يَقْضِ مَآ اَمَرَهٗۗ", "Sekali-kali jangan (begitu)! Dia (manusia) itu belum melaksanakan apa yang Dia (Allah) perintahkan kepadanya.", "kallā lammā yaqḍi mā amarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5781,80,'`Abasa',24,"فَلْيَنْظُرِ الْاِنْسَانُ اِلٰى طَعَامِهٖٓ ۙ", "Maka hendaklah manusia itu memperhatikan makanannya.", "falyanẓuril-insānu ilā ṭa'āmih");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5782,80,'`Abasa',25,"اَنَّا صَبَبْنَا الْمَاۤءَ صَبًّاۙ", "Kamilah yang telah mencurahkan air melimpah (dari langit),", "annā ṣababnal-mā`a ṣabbā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5783,80,'`Abasa',26,"ثُمَّ شَقَقْنَا الْاَرْضَ شَقًّاۙ", "kemudian Kami belah bumi dengan sebaik-baiknya,", "ṡumma syaqaqnal-arḍa syaqqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5784,80,'`Abasa',27,"فَاَنْۢبَتْنَا فِيْهَا حَبًّاۙ", "lalu di sana Kami tumbuhkan biji-bijian,", "fa ambatnā fīhā ḥabbā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5785,80,'`Abasa',28,"وَّعِنَبًا وَّقَضْبًاۙ", "dan anggur dan sayur-sayuran,", "wa 'inabaw wa qaḍbā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5786,80,'`Abasa',29,"وَّزَيْتُوْنًا وَّنَخْلًاۙ", "dan zaitun dan pohon kurma,", "wa zaitụnaw wa nakhlā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5787,80,'`Abasa',30,"وَّحَدَاۤئِقَ غُلْبًا", "dan kebun-kebun (yang) rindang,", "wa ḥadā`iqa gulbā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5788,80,'`Abasa',31,"وَفَاكِهَةً وَّاَبًّا", "dan buah-buahan serta rerumputan.", "wa fākihataw wa abbā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5789,80,'`Abasa',32,"مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ", "(Semua itu) untuk kesenanganmu dan untuk hewan-hewan ternakmu.", "matā'al lakum wa li`an'āmikum");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5790,80,'`Abasa',33,"فَاِذَا جَاۤءَتِ الصَّاۤخَّةُ ۖ", "Maka apabila datang suara yang memekakkan (tiupan sangkakala yang kedua),", "fa iżā jā`atiṣ-ṣākhkhah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5791,80,'`Abasa',34,"يَوْمَ يَفِرُّ الْمَرْءُ مِنْ اَخِيْهِۙ", "pada hari itu manusia lari dari saudaranya,", "yauma yafirrul-mar`u min akhīh");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5792,80,'`Abasa',35,"وَاُمِّهٖ وَاَبِيْهِۙ", "dan dari ibu dan bapaknya,", "wa ummihī wa abīh");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5793,80,'`Abasa',36,"وَصَاحِبَتِهٖ وَبَنِيْهِۗ", "dan dari istri dan anak-anaknya.", "wa ṣāḥibatihī wa banīh");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5794,80,'`Abasa',37,"لِكُلِّ امْرِئٍ مِّنْهُمْ يَوْمَىِٕذٍ شَأْنٌ يُّغْنِيْهِۗ", "Setiap orang dari mereka pada hari itu mempunyai urusan yang menyibukkannya.", "likullimri`im min-hum yauma`iżin sya`nuy yugnīh");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5795,80,'`Abasa',38,"وُجُوْهٌ يَّوْمَىِٕذٍ مُّسْفِرَةٌۙ", "Pada hari itu ada wajah-wajah yang berseri-seri,", "wujụhuy yauma`iżim musfirah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5796,80,'`Abasa',39,"ضَاحِكَةٌ مُّسْتَبْشِرَةٌ ۚ", "tertawa dan gembira ria,", "ḍāḥikatum mustabsyirah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5797,80,'`Abasa',40,"وَوُجُوْهٌ يَّوْمَىِٕذٍ عَلَيْهَا غَبَرَةٌۙ", "dan pada hari itu ada (pula) wajah-wajah yang tertutup debu (suram),", "wa wujụhuy yauma`iżin 'alaihā gabarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5798,80,'`Abasa',41,"تَرْهَقُهَا قَتَرَةٌ ۗ", "tertutup oleh kegelapan (ditimpa kehinaan dan kesusahan).", "tarhaquhā qatarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5799,80,'`Abasa',42,"اُولٰۤىِٕكَ هُمُ الْكَفَرَةُ الْفَجَرَةُ", "Mereka itulah orang-orang kafir yang durhaka.", "ulā`ika humul-kafaratul-fajarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5800,81,'At-takwir',1,"اِذَا الشَّمْسُ كُوِّرَتْۖ", "Apabila matahari digulung,", "iżasy-syamsu kuwwirat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5801,81,'At-takwir',2,"وَاِذَا النُّجُوْمُ انْكَدَرَتْۖ", "dan apabila bintang-bintang berjatuhan,", "wa iżan-nujụmungkadarat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5802,81,'At-takwir',3,"وَاِذَا الْجِبَالُ سُيِّرَتْۖ", "dan apabila gunung-gunung dihancurkan,", "wa iżal-jibālu suyyirat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5803,81,'At-takwir',4,"وَاِذَا الْعِشَارُ عُطِّلَتْۖ", "dan apabila unta-unta yang bunting ditinggalkan (tidak terurus),", "wa iżal-'isyāru 'uṭṭilat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5804,81,'At-takwir',5,"وَاِذَا الْوُحُوْشُ حُشِرَتْۖ", "dan apabila binatang-binatang liar dikumpulkan,", "wa iżal-wuḥụsyu ḥusyirat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5805,81,'At-takwir',6,"وَاِذَا الْبِحَارُ سُجِّرَتْۖ", "dan apabila lautan dipanaskan,", "wa iżal-biḥāru sujjirat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5806,81,'At-takwir',7,"وَاِذَا النُّفُوْسُ زُوِّجَتْۖ", "dan apabila roh-roh dipertemukan (dengan tubuh),", "wa iżan-nufụsu zuwwijat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5807,81,'At-takwir',8,"وَاِذَا الْمَوْءٗدَةُ سُىِٕلَتْۖ", "dan apabila bayi-bayi perempuan yang dikubur hidup-hidup ditanya,", "wa iżal-mau`ụdatu su`ilat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5808,81,'At-takwir',9,"بِاَيِّ ذَنْۢبٍ قُتِلَتْۚ", "karena dosa apa dia dibunuh?", "bi`ayyi żambing qutilat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5809,81,'At-takwir',10,"وَاِذَا الصُّحُفُ نُشِرَتْۖ", "Dan apabila lembaran-lembaran (catatan amal) telah dibuka lebar-lebar,", "wa iżaṣ-ṣuḥufu nusyirat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5810,81,'At-takwir',11,"وَاِذَا السَّمَاۤءُ كُشِطَتْۖ", "dan apabila langit dilenyapkan,", "wa iżas-samā`u kusyiṭat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5811,81,'At-takwir',12,"وَاِذَا الْجَحِيْمُ سُعِّرَتْۖ", "dan apabila neraka Jahim dinyalakan,", "wa iżal-jaḥīmu su''irat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5812,81,'At-takwir',13,"وَاِذَا الْجَنَّةُ اُزْلِفَتْۖ", "dan apabila surga didekatkan,", "wa iżal-jannatu uzlifat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5813,81,'At-takwir',14,"عَلِمَتْ نَفْسٌ مَّآ اَحْضَرَتْۗ", "setiap jiwa akan mengetahui apa yang telah dikerjakannya.", "'alimat nafsum mā aḥḍarat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5814,81,'At-takwir',15,"فَلَآ اُقْسِمُ بِالْخُنَّسِۙ", "Aku bersumpah demi bintang-bintang,", "fa lā uqsimu bil-khunnas");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5815,81,'At-takwir',16,"الْجَوَارِ الْكُنَّسِۙ", "yang beredar dan terbenam,", "al-jawāril-kunnas");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5816,81,'At-takwir',17,"وَالَّيْلِ اِذَا عَسْعَسَۙ", "demi malam apabila telah larut,", "wal-laili iżā 'as'as");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5817,81,'At-takwir',18,"وَالصُّبْحِ اِذَا تَنَفَّسَۙ", "dan demi subuh apabila fajar telah menyingsing,", "waṣ-ṣub-ḥi iżā tanaffas");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5818,81,'At-takwir',19,"اِنَّهٗ لَقَوْلُ رَسُوْلٍ كَرِيْمٍۙ", "sesungguhnya (Al-Qur'an) itu benar-benar firman (Allah yang dibawa oleh) utusan yang mulia (Jibril),", "innahụ laqaulu rasụling karīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5819,81,'At-takwir',20,"ذِيْ قُوَّةٍ عِنْدَ ذِى الْعَرْشِ مَكِيْنٍۙ", "yang memiliki kekuatan, memiliki kedudukan tinggi di sisi (Allah) yang memiliki ‘Arsy,", "żī quwwatin 'inda żil-'arsyi makīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5820,81,'At-takwir',21,"مُّطَاعٍ ثَمَّ اَمِيْنٍۗ", "yang di sana (di alam malaikat) ditaati dan dipercaya.", "muṭā'in ṡamma amīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5821,81,'At-takwir',22,"وَمَا صَاحِبُكُمْ بِمَجْنُوْنٍۚ", "Dan temanmu (Muhammad) itu bukanlah orang gila.", "wa mā ṣāḥibukum bimajnụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5822,81,'At-takwir',23,"وَلَقَدْ رَاٰهُ بِالْاُفُقِ الْمُبِيْنِۚ", "Dan sungguh, dia (Muhammad) telah melihatnya (Jibril) di ufuk yang terang.", "wa laqad ra`āhu bil-ufuqil-mubīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5823,81,'At-takwir',24,"وَمَا هُوَ عَلَى الْغَيْبِ بِضَنِيْنٍۚ", "Dan dia (Muhammad) bukanlah seorang yang kikir (enggan) untuk menerangkan yang gaib.", "wa mā huwa 'alal-gaibi biḍanīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5824,81,'At-takwir',25,"وَمَا هُوَ بِقَوْلِ شَيْطٰنٍ رَّجِيْمٍۚ", "Dan (Al-Qur'an) itu bukanlah perkataan setan yang terkutuk,", "wa mā huwa biqauli syaiṭānir rajīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5825,81,'At-takwir',26,"فَاَيْنَ تَذْهَبُوْنَۗ", "maka ke manakah kamu akan pergi?", "fa aina taż-habụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5826,81,'At-takwir',27,"اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَۙ", "(Al-Qur'an) itu tidak lain adalah peringatan bagi seluruh alam,", "in huwa illā żikrul lil-'ālamīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5827,81,'At-takwir',28,"لِمَنْ شَاۤءَ مِنْكُمْ اَنْ يَّسْتَقِيْمَۗ", "(yaitu) bagi siapa di antara kamu yang menghendaki menempuh jalan yang lurus.", "liman syā`a mingkum ay yastaqīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5828,81,'At-takwir',29,"وَمَا تَشَاۤءُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ", "Dan kamu tidak dapat menghendaki (menempuh jalan itu) kecuali apabila dikehendaki Allah, Tuhan seluruh alam.", "wa mā tasyā`ụna illā ay yasyā`allāhu rabbul-'ālamīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5829,82,'Al-Infithar',1,"اِذَا السَّمَاۤءُ انْفَطَرَتْۙ", "Apabila langit terbelah,", "iżas-samā`unfaṭarat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5830,82,'Al-Infithar',2,"وَاِذَا الْكَوَاكِبُ انْتَثَرَتْۙ", "dan apabila bintang-bintang jatuh berserakan,", "wa iżal-kawākibuntaṡarat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5831,82,'Al-Infithar',3,"وَاِذَا الْبِحَارُ فُجِّرَتْۙ", "dan apabila lautan dijadikan meluap,", "wa iżal-biḥāru fujjirat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5832,82,'Al-Infithar',4,"وَاِذَا الْقُبُوْرُ بُعْثِرَتْۙ", "dan apabila kuburan-kuburan dibongkar,", "wa iżal-qubụru bu'ṡirat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5833,82,'Al-Infithar',5,"عَلِمَتْ نَفْسٌ مَّا قَدَّمَتْ وَاَخَّرَتْۗ", "(maka) setiap jiwa akan mengetahui apa yang telah dikerjakan dan yang dilalaikan(nya).", "'alimat nafsum mā qaddamat wa akhkharat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5834,82,'Al-Infithar',6,"يٰٓاَيُّهَا الْاِنْسَانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيْمِۙ", "Wahai manusia! Apakah yang telah memperdayakan kamu (berbuat durhaka) terhadap Tuhanmu Yang Mahamulia,", "yā ayyuhal-insānu mā garraka birabbikal-karīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5835,82,'Al-Infithar',7,"الَّذِيْ خَلَقَكَ فَسَوّٰىكَ فَعَدَلَكَۙ", "yang telah menciptakanmu lalu menyempurnakan kejadianmu dan menjadikan (susunan tubuh)mu seimbang,", "allażī khalaqaka fa sawwāka fa 'adalak");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5836,82,'Al-Infithar',8,"فِيْٓ اَيِّ صُوْرَةٍ مَّا شَاۤءَ رَكَّبَكَۗ", "dalam bentuk apa saja yang dikehendaki, Dia menyusun tubuhmu.", "fī ayyi ṣụratim mā syā`a rakkabak");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5837,82,'Al-Infithar',9,"كَلَّا بَلْ تُكَذِّبُوْنَ بِالدِّيْنِۙ", "Sekali-kali jangan begitu! Bahkan kamu mendustakan hari pembalasan.", "kallā bal tukażżibụna bid-dīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5838,82,'Al-Infithar',10,"وَاِنَّ عَلَيْكُمْ لَحٰفِظِيْنَۙ", "Dan sesungguhnya bagi kamu ada (malaikat-malaikat) yang mengawasi (pekerjaanmu),", "wa inna 'alaikum laḥāfiẓīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5839,82,'Al-Infithar',11,"كِرَامًا كَاتِبِيْنَۙ", "yang mulia (di sisi Allah) dan yang mencatat (amal perbuatanmu),", "kirāmang kātibīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5840,82,'Al-Infithar',12,"يَعْلَمُوْنَ مَا تَفْعَلُوْنَ", "mereka mengetahui apa yang kamu kerjakan.", "ya'lamụna mā taf'alụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5841,82,'Al-Infithar',13,"اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ", "Sesungguhnya orang-orang yang berbakti benar-benar berada dalam (surga yang penuh) kenikmatan,", "innal-abrāra lafī na'īm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5842,82,'Al-Infithar',14,"وَّاِنَّ الْفُجَّارَ لَفِيْ جَحِيْمٍ", "dan sesungguhnya orang-orang yang durhaka benar-benar berada dalam neraka.", "wa innal-fujjāra lafī jaḥīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5843,82,'Al-Infithar',15,"يَصْلَوْنَهَا يَوْمَ الدِّيْنِ", "Mereka masuk ke dalamnya pada hari pembalasan.", "yaṣlaunahā yaumad-dīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5844,82,'Al-Infithar',16,"وَمَا هُمْ عَنْهَا بِغَاۤىِٕبِيْنَۗ", "Dan mereka tidak mungkin keluar dari neraka itu.", "wa mā hum 'an-hā bigā`ibīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5845,82,'Al-Infithar',17,"وَمَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۙ", "Dan tahukah kamu apakah hari pembalasan itu?", "wa mā adrāka mā yaumud-dīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5846,82,'Al-Infithar',18,"ثُمَّ مَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۗ", "Sekali lagi, tahukah kamu apakah hari pembalasan itu?", "ṡumma mā adrāka mā yaumud-dīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5847,82,'Al-Infithar',19,"يَوْمَ لَا تَمْلِكُ نَفْسٌ لِّنَفْسٍ شَيْـًٔا ۗوَالْاَمْرُ يَوْمَىِٕذٍ لِّلّٰهِ", "(Yaitu) pada hari (ketika) seseorang sama sekali tidak berdaya (menolong) orang lain. Dan segala urusan pada hari itu dalam kekuasaan Allah.", "yauma lā tamliku nafsul linafsin syai`ā, wal-amru yauma`iżil lillāh");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5848,83,'Al-Muthaffifin',1,"وَيْلٌ لِّلْمُطَفِّفِيْنَۙ", "Celakalah bagi orang-orang yang curang (dalam menakar dan menimbang)!", "wailul lil-muṭaffifīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5849,83,'Al-Muthaffifin',2,"الَّذِيْنَ اِذَا اكْتَالُوْا عَلَى النَّاسِ يَسْتَوْفُوْنَۖ", "(Yaitu) orang-orang yang apabila menerima takaran dari orang lain mereka minta dicukupkan,", "allażīna iżaktālụ 'alan-nāsi yastaufụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5850,83,'Al-Muthaffifin',3,"وَاِذَا كَالُوْهُمْ اَوْ وَّزَنُوْهُمْ يُخْسِرُوْنَۗ", "dan apabila mereka menakar atau menimbang (untuk orang lain), mereka mengurangi.", "wa iżā kālụhum aw wazanụhum yukhsirụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5851,83,'Al-Muthaffifin',4,"اَلَا يَظُنُّ اُولٰۤىِٕكَ اَنَّهُمْ مَّبْعُوْثُوْنَۙ", "Tidakkah mereka itu mengira, bahwa sesungguhnya mereka akan dibangkitkan,", "alā yaẓunnu ulā`ika annahum mab'ụṡụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5852,83,'Al-Muthaffifin',5,"لِيَوْمٍ عَظِيْمٍۙ", "pada suatu hari yang besar,", "liyaumin 'aẓīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5853,83,'Al-Muthaffifin',6,"يَّوْمَ يَقُوْمُ النَّاسُ لِرَبِّ الْعٰلَمِيْنَۗ", "(yaitu) pada hari (ketika) semua orang bangkit menghadap Tuhan seluruh alam.", "yauma yaqụmun-nāsu lirabbil-'ālamīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5854,83,'Al-Muthaffifin',7,"كَلَّآ اِنَّ كِتٰبَ الْفُجَّارِ لَفِيْ سِجِّيْنٍۗ", "Sekali-kali jangan begitu! Sesungguhnya catatan orang yang durhaka benar-benar tersimpan dalam Sijjin.", "kallā inna kitābal-fujjāri lafī sijjīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5855,83,'Al-Muthaffifin',8,"وَمَآ اَدْرٰىكَ مَا سِجِّيْنٌۗ", "Dan tahukah engkau apakah Sijjin itu?", "wa mā adrāka mā sijjīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5856,83,'Al-Muthaffifin',9,"كِتٰبٌ مَّرْقُوْمٌۗ", "(Yaitu) Kitab yang berisi catatan (amal).", "kitābum marqụm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5857,83,'Al-Muthaffifin',10,"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَۙ", "Celakalah pada hari itu, bagi orang-orang yang mendustakan!", "wailuy yauma`iżil lil-mukażżibīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5858,83,'Al-Muthaffifin',11,"الَّذِيْنَ يُكَذِّبُوْنَ بِيَوْمِ الدِّيْنِۗ", "(yaitu) orang-orang yang mendustakannya (hari pembalasan).", "allażīna yukażżibụna biyaumid-dīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5859,83,'Al-Muthaffifin',12,"وَمَا يُكَذِّبُ بِهٖٓ اِلَّا كُلُّ مُعْتَدٍ اَثِيْمٍۙ", "Dan tidak ada yang mendustakannya (hari pembalasan) kecuali setiap orang yang melampaui batas dan berdosa,", "wa mā yukażżibu bihī illā kullu mu'tadin aṡīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5860,83,'Al-Muthaffifin',13,"اِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا قَالَ اَسَاطِيْرُ الْاَوَّلِيْنَۗ", "yang apabila dibacakan kepadanya ayat-ayat Kami, dia berkata, “Itu adalah dongeng orang-orang dahulu.”", "iżā tutlā 'alaihi āyātunā qāla asāṭīrul-awwalīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5861,83,'Al-Muthaffifin',14,"كَلَّا بَلْ ۜرَانَ عَلٰى قُلُوْبِهِمْ مَّا كَانُوْا يَكْسِبُوْنَ", "Sekali-kali tidak! Bahkan apa yang mereka kerjakan itu telah menutupi hati mereka.", "kallā bal rāna 'alā qulụbihim mā kānụ yaksibụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5862,83,'Al-Muthaffifin',15,"كَلَّآ اِنَّهُمْ عَنْ رَّبِّهِمْ يَوْمَىِٕذٍ لَّمَحْجُوْبُوْنَۗ", "Sekali-kali tidak! Sesungguhnya mereka pada hari itu benar-benar terhalang dari (melihat) Tuhannya.", "kallā innahum 'ar rabbihim yauma`iżil lamaḥjụbụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5863,83,'Al-Muthaffifin',16,"ثُمَّ اِنَّهُمْ لَصَالُوا الْجَحِيْمِۗ", "Kemudian, sesungguhnya mereka benar-benar masuk neraka.", "ṡumma innahum laṣālul-jaḥīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5864,83,'Al-Muthaffifin',17,"ثُمَّ يُقَالُ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَۗ", "Kemudian, dikatakan (kepada mereka), “Inilah (azab) yang dahulu kamu dustakan.”", "ṡumma yuqālu hāżallażī kuntum bihī tukażżibụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5865,83,'Al-Muthaffifin',18,"كَلَّآ اِنَّ كِتٰبَ الْاَبْرَارِ لَفِيْ عِلِّيِّيْنَۗ", "Sekali-kali tidak! Sesungguhnya catatan orang-orang yang berbakti benar-benar tersimpan dalam ’Illiyyin.", "kallā inna kitābal-abrāri lafī 'illiyyīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5866,83,'Al-Muthaffifin',19,"وَمَآ اَدْرٰىكَ مَا عِلِّيُّوْنَۗ", "Dan tahukah engkau apakah ’Illiyyin itu?", "wa mā adrāka mā 'illiyyụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5867,83,'Al-Muthaffifin',20,"كِتٰبٌ مَّرْقُوْمٌۙ", "(Yaitu) Kitab yang berisi catatan (amal),", "kitābum marqụm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5868,83,'Al-Muthaffifin',21,"يَّشْهَدُهُ الْمُقَرَّبُوْنَۗ", "yang disaksikan oleh (malaikat-malaikat) yang didekatkan (kepada Allah).", "yasy-haduhul-muqarrabụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5869,83,'Al-Muthaffifin',22,"اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ", "Sesungguhnya orang-orang yang berbakti benar-benar berada dalam (surga yang penuh) kenikmatan,", "innal-abrāra lafī na'īm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5870,83,'Al-Muthaffifin',23,"عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۙ", "mereka (duduk) di atas dipan-dipan melepas pandangan.", "'alal-arā`iki yanẓurụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5871,83,'Al-Muthaffifin',24,"تَعْرِفُ فِيْ وُجُوْهِهِمْ نَضْرَةَ النَّعِيْمِۚ", "Kamu dapat mengetahui dari wajah mereka kesenangan hidup yang penuh kenikmatan.", "ta'rifu fī wujụhihim naḍratan na'īm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5872,83,'Al-Muthaffifin',25,"يُسْقَوْنَ مِنْ رَّحِيْقٍ مَّخْتُوْمٍۙ", "Mereka diberi minum dari khamar murni (tidak memabukkan) yang (tempatnya) masih dilak (disegel),", "yusqauna mir raḥīqim makhtụm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5873,83,'Al-Muthaffifin',26,"خِتٰمُهٗ مِسْكٌ ۗوَفِيْ ذٰلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُوْنَۗ", "laknya dari kasturi. Dan untuk yang demikian itu hendaknya orang berlomba-lomba.", "khitāmuhụ misk, wa fī żālika falyatanāfasil-mutanāfisụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5874,83,'Al-Muthaffifin',27,"وَمِزَاجُهٗ مِنْ تَسْنِيْمٍۙ", "Dan campurannya dari tasnim,", "wa mizājuhụ min tasnīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5875,83,'Al-Muthaffifin',28,"عَيْنًا يَّشْرَبُ بِهَا الْمُقَرَّبُوْنَۗ", "(yaitu) mata air yang diminum oleh mereka yang dekat (kepada Allah).", "'ainay yasyrabu bihal-muqarrabụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5876,83,'Al-Muthaffifin',29,"اِنَّ الَّذِيْنَ اَجْرَمُوْا كَانُوْا مِنَ الَّذِيْنَ اٰمَنُوْا يَضْحَكُوْنَۖ", "Sesungguhnya orang-orang yang berdosa adalah mereka yang dahulu menertawakan orang-orang yang beriman.", "innallażīna ajramụ kānụ minallażīna āmanụ yaḍ-ḥakụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5877,83,'Al-Muthaffifin',30,"وَاِذَا مَرُّوْا بِهِمْ يَتَغَامَزُوْنَۖ", "Dan apabila mereka (orang-orang yang beriman) melintas di hadapan mereka, mereka saling mengedip-ngedipkan matanya,", "wa iżā marrụ bihim yatagāmazụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5878,83,'Al-Muthaffifin',31,"وَاِذَا انْقَلَبُوْٓا اِلٰٓى اَهْلِهِمُ انْقَلَبُوْا فَكِهِيْنَۖ", "dan apabila kembali kepada kaumnya, mereka kembali dengan gembira ria.", "wa iżangqalabū ilā ahlihimungqalabụ fakihīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5879,83,'Al-Muthaffifin',32,"وَاِذَا رَاَوْهُمْ قَالُوْٓا اِنَّ هٰٓؤُلَاۤءِ لَضَاۤلُّوْنَۙ", "Dan apabila mereka melihat (orang-orang mukmin), mereka mengatakan, “Sesungguhnya mereka benar-benar orang-orang sesat,”", "wa iżā ra`auhum qālū inna hā`ulā`i laḍāllụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5880,83,'Al-Muthaffifin',33,"وَمَآ اُرْسِلُوْا عَلَيْهِمْ حٰفِظِيْنَۗ", "padahal (orang-orang yang berdosa itu), mereka tidak diutus sebagai penjaga (orang-orang mukmin).", "wa mā ursilụ 'alaihim ḥāfiẓīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5881,83,'Al-Muthaffifin',34,"فَالْيَوْمَ الَّذِيْنَ اٰمَنُوْا مِنَ الْكُفَّارِ يَضْحَكُوْنَۙ", "Maka pada hari ini, orang-orang yang beriman yang menertawakan orang-orang kafir,", "fal-yaumallażīna āmanụ minal-kuffāri yaḍ-ḥakụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5882,83,'Al-Muthaffifin',35,"عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۗ", "mereka (duduk) di atas dipan-dipan melepas pandangan.", "'alal-arā`iki yanẓurụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5883,83,'Al-Muthaffifin',36,"هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوْا يَفْعَلُوْنَ", "Apakah orang-orang kafir itu diberi balasan (hukuman) terhadap apa yang telah mereka perbuat?", "hal ṡuwwibal-kuffāru mā kānụ yaf'alụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5884,84,'Al-Insyiqaq',1,"اِذَا السَّمَاۤءُ انْشَقَّتْۙ", "Apabila langit terbelah,", "iżas-samā`unsyaqqat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5885,84,'Al-Insyiqaq',2,"وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۙ", "dan patuh kepada Tuhannya, dan sudah semestinya patuh,", "wa ażinat lirabbihā wa ḥuqqat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5886,84,'Al-Insyiqaq',3,"وَاِذَا الْاَرْضُ مُدَّتْۙ", "dan apabila bumi diratakan,", "wa iżal-arḍu muddat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5887,84,'Al-Insyiqaq',4,"وَاَلْقَتْ مَا فِيْهَا وَتَخَلَّتْۙ", "dan memuntahkan apa yang ada di dalamnya dan menjadi kosong,", "wa alqat mā fīhā wa takhallat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5888,84,'Al-Insyiqaq',5,"وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۗ", "dan patuh kepada Tuhannya, dan sudah semestinya patuh.", "wa ażinat lirabbihā wa ḥuqqat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5889,84,'Al-Insyiqaq',6,"يٰٓاَيُّهَا الْاِنْسَانُ اِنَّكَ كَادِحٌ اِلٰى رَبِّكَ كَدْحًا فَمُلٰقِيْهِۚ", "Wahai manusia! Sesungguhnya kamu telah bekerja keras menuju Tuhanmu, maka kamu akan menemui-Nya.", "yā ayyuhal-insānu innaka kādiḥun ilā rabbika kad-ḥan fa mulāqīh");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5890,84,'Al-Insyiqaq',7,"فَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖۙ", "Maka adapun orang yang catatannya diberikan dari sebelah kanannya,", "fa ammā man ụtiya kitābahụ biyamīnih");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5891,84,'Al-Insyiqaq',8,"فَسَوْفَ يُحَاسَبُ حِسَابًا يَّسِيْرًاۙ", "maka dia akan diperiksa dengan pemeriksaan yang mudah,", "fa saufa yuḥāsabu ḥisābay yasīrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5892,84,'Al-Insyiqaq',9,"وَّيَنْقَلِبُ اِلٰٓى اَهْلِهٖ مَسْرُوْرًاۗ", "dan dia akan kembali kepada keluarganya (yang sama-sama beriman) dengan gembira.", "wa yangqalibu ilā ahlihī masrụrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5893,84,'Al-Insyiqaq',10,"وَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ وَرَاۤءَ ظَهْرِهٖۙ", "Dan adapun orang yang catatannya diberikan dari sebelah belakang,", "wa ammā man ụtiya kitābahụ warā`a ẓahrih");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5894,84,'Al-Insyiqaq',11,"فَسَوْفَ يَدْعُوْ ثُبُوْرًاۙ", "maka dia akan berteriak, “Celakalah aku!”", "fa saufa yad'ụ ṡubụrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5895,84,'Al-Insyiqaq',12,"وَّيَصْلٰى سَعِيْرًاۗ", "Dan dia akan masuk ke dalam api yang menyala-nyala (neraka).", "wa yaṣlā sa'īrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5896,84,'Al-Insyiqaq',13,"اِنَّهٗ كَانَ فِيْٓ اَهْلِهٖ مَسْرُوْرًاۗ", "Sungguh, dia dahulu (di dunia) bergembira di kalangan keluarganya (yang sama-sama kafir).", "innahụ kāna fī ahlihī masrụrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5897,84,'Al-Insyiqaq',14,"اِنَّهٗ ظَنَّ اَنْ لَّنْ يَّحُوْرَ ۛ", "Sesungguhnya dia mengira bahwa dia tidak akan kembali (kepada Tuhannya).", "innahụ ẓanna al lay yaḥụr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5898,84,'Al-Insyiqaq',15,"بَلٰىۛ اِنَّ رَبَّهٗ كَانَ بِهٖ بَصِيْرًاۗ", "Tidak demikian, sesungguhnya Tuhannya selalu melihatnya.", "balā inna rabbahụ kāna bihī baṣīrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5899,84,'Al-Insyiqaq',16,"فَلَآ اُقْسِمُ بِالشَّفَقِۙ", "Maka Aku bersumpah demi cahaya merah pada waktu senja,", "fa lā uqsimu bisy-syafaq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5900,84,'Al-Insyiqaq',17,"وَالَّيْلِ وَمَا وَسَقَۙ", "demi malam dan apa yang diselubunginya,", "wal-laili wa mā wasaq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5901,84,'Al-Insyiqaq',18,"وَالْقَمَرِ اِذَا اتَّسَقَۙ", "demi bulan apabila jadi purnama,", "wal-qamari iżattasaq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5902,84,'Al-Insyiqaq',19,"لَتَرْكَبُنَّ طَبَقًا عَنْ طَبَقٍۗ", "sungguh, akan kamu jalani tingkat demi tingkat (dalam kehidupan).", "latarkabunna ṭabaqan 'an ṭabaq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5903,84,'Al-Insyiqaq',20,"فَمَا لَهُمْ لَا يُؤْمِنُوْنَۙ", "Maka mengapa mereka tidak mau beriman?", "fa mā lahum lā yu`minụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5904,84,'Al-Insyiqaq',21,"وَاِذَا قُرِئَ عَلَيْهِمُ الْقُرْاٰنُ لَا يَسْجُدُوْنَ ۗ ۩", "Dan apabila Al-Qur'an dibacakan kepada mereka, mereka tidak (mau) bersujud,", "wa iżā quri`a 'alaihimul-qur`ānu lā yasjudụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5905,84,'Al-Insyiqaq',22,"بَلِ الَّذِيْنَ كَفَرُوْا يُكَذِّبُوْنَۖ", "bahkan orang-orang kafir itu mendustakan(nya).", "balillażīna kafarụ yukażżibụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5906,84,'Al-Insyiqaq',23,"وَاللّٰهُ اَعْلَمُ بِمَا يُوْعُوْنَۖ", "Dan Allah lebih mengetahui apa yang mereka sembunyikan (dalam hati mereka).", "wallāhu a'lamu bimā yụ'ụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5907,84,'Al-Insyiqaq',24,"فَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍۙ", "Maka sampaikanlah kepada mereka (ancaman) azab yang pedih,", "fa basysyir-hum bi'ażābin alīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5908,84,'Al-Insyiqaq',25,"اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍ", "kecuali orang-orang yang beriman dan mengerjakan kebajikan, mereka akan mendapat pahala yang tidak putus-putusnya.", "illallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lahum ajrun gairu mamnụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5909,85,'Al-Buruj',1,"وَالسَّمَاۤءِ ذَاتِ الْبُرُوْجِۙ", "Demi langit yang mempunyai gugusan bintang,", "was-samā`i żātil-burụj");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5910,85,'Al-Buruj',2,"وَالْيَوْمِ الْمَوْعُوْدِۙ", "dan demi hari yang dijanjikan.", "wal-yaumil-mau'ụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5911,85,'Al-Buruj',3,"وَشَاهِدٍ وَّمَشْهُوْدٍۗ", "Demi yang menyaksikan dan yang disaksikan.", "wa syāhidiw wa masy-hụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5912,85,'Al-Buruj',4,"قُتِلَ اَصْحٰبُ الْاُخْدُوْدِۙ", "Binasalah orang-orang yang membuat parit (yaitu para pembesar Najran di Yaman),", "qutila aṣ-ḥābul-ukhdụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5913,85,'Al-Buruj',5,"النَّارِ ذَاتِ الْوَقُوْدِۙ", "yang berapi (yang mempunyai) kayu bakar,", "an-nāri żātil-waqụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5914,85,'Al-Buruj',6,"اِذْ هُمْ عَلَيْهَا قُعُوْدٌۙ", "ketika mereka duduk di sekitarnya,", "iż hum 'alaihā qu'ụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5915,85,'Al-Buruj',7,"وَّهُمْ عَلٰى مَا يَفْعَلُوْنَ بِالْمُؤْمِنِيْنَ شُهُوْدٌ ۗ", "sedang mereka menyaksikan apa yang mereka perbuat terhadap orang-orang mukmin.", "wa hum 'alā mā yaf'alụna bil-mu`minīna syuhụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5916,85,'Al-Buruj',8,"وَمَا نَقَمُوْا مِنْهُمْ اِلَّآ اَنْ يُّؤْمِنُوْا بِاللّٰهِ الْعَزِيْزِ الْحَمِيْدِۙ", "Dan mereka menyiksa orang-orang mukmin itu hanya karena (orang-orang mukmin itu) beriman kepada Allah Yang Mahaperkasa, Maha Terpuji,", "wa mā naqamụ min-hum illā ay yu`minụ billāhil-'azīzil-ḥamīd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5917,85,'Al-Buruj',9,"الَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ۗ", "yang memiliki kerajaan langit dan bumi. Dan Allah Maha Menyaksikan segala sesuatu.", "allażī lahụ mulkus-samāwāti wal-arḍ, wallāhu 'alā kulli syai`in syahīd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5918,85,'Al-Buruj',10,"اِنَّ الَّذِيْنَ فَتَنُوا الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ ثُمَّ لَمْ يَتُوْبُوْا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَرِيْقِۗ", "Sungguh, orang-orang yang mendatangkan cobaan (bencana, membunuh, menyiksa) kepada orang-orang mukmin laki-laki dan perempuan lalu mereka tidak bertobat, maka mereka akan mendapat azab Jahanam dan mereka akan mendapat azab (neraka) yang membakar.", "innallażīna fatanul-mu`minīna wal-mu`mināti ṡumma lam yatụbụ fa lahum 'ażābu jahannama wa lahum 'ażābul-ḥarīq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5919,85,'Al-Buruj',11,"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ەۗ ذٰلِكَ الْفَوْزُ الْكَبِيْرُۗ", "Sungguh, orang-orang yang beriman dan mengerjakan kebajikan, mereka akan mendapat surga yang mengalir di bawahnya sungai-sungai, itulah kemenangan yang agung.", "innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lahum jannātun tajrī min taḥtihal-an-hār, żālikal-fauzul-kabīr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5920,85,'Al-Buruj',12,"اِنَّ بَطْشَ رَبِّكَ لَشَدِيْدٌ ۗ", "Sungguh, azab Tuhanmu sangat keras.", "inna baṭsya rabbika lasyadīd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5921,85,'Al-Buruj',13,"اِنَّهٗ هُوَ يُبْدِئُ وَيُعِيْدُۚ", "Sungguh, Dialah yang memulai pen-ciptaan (makhluk) dan yang menghidupkannya (kembali).", "innahụ huwa yubdi`u wa yu'īd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5922,85,'Al-Buruj',14,"وَهُوَ الْغَفُوْرُ الْوَدُوْدُۙ", "Dan Dialah Yang Maha Pengampun, Maha Pengasih,", "wa huwal-gafụrul-wadụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5923,85,'Al-Buruj',15,"ذُو الْعَرْشِ الْمَجِيْدُۙ", "yang memiliki ‘Arsy, lagi Mahamulia,", "żul-'arsyil-majīd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5924,85,'Al-Buruj',16,"فَعَّالٌ لِّمَا يُرِيْدُۗ", "Mahakuasa berbuat apa yang Dia kehendaki.", "fa''ālul limā yurīd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5925,85,'Al-Buruj',17,"هَلْ اَتٰىكَ حَدِيْثُ الْجُنُوْدِۙ", "Sudahkah sampai kepadamu berita tentang bala tentara (penentang),", "hal atāka ḥadīṡul-junụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5926,85,'Al-Buruj',18,"فِرْعَوْنَ وَثَمُوْدَۗ", "(yaitu) Fir‘aun dan Samud?", "fir'auna wa ṡamụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5927,85,'Al-Buruj',19,"بَلِ الَّذِيْنَ كَفَرُوْا فِيْ تَكْذِيْبٍۙ", "Memang orang-orang kafir (selalu) mendustakan,", "balillażīna kafarụ fī takżīb");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5928,85,'Al-Buruj',20,"وَّاللّٰهُ مِنْ وَّرَاۤىِٕهِمْ مُّحِيْطٌۚ", "padahal Allah mengepung dari belakang mereka (sehingga tidak dapat lolos).", "wallāhu miw warā`ihim muḥīṭ");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5929,85,'Al-Buruj',21,"بَلْ هُوَ قُرْاٰنٌ مَّجِيْدٌۙ", "Bahkan (yang didustakan itu) ialah Al-Qur'an yang mulia,", "bal huwa qur`ānum majīd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5930,85,'Al-Buruj',22,"فِيْ لَوْحٍ مَّحْفُوْظٍ", "yang (tersimpan) dalam (tempat) yang terjaga (Lauh Mahfuzh).", "fī lauḥim maḥfụẓ");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5931,86,'Ath-Thariq',1,"وَالسَّمَاۤءِ وَالطَّارِقِۙ", "Demi langit dan yang datang pada malam hari.", "was-samā`i waṭ-ṭāriq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5932,86,'Ath-Thariq',2,"وَمَآ اَدْرٰىكَ مَا الطَّارِقُۙ", "Dan tahukah kamu apakah yang datang pada malam hari itu?", "wa mā adrāka maṭ-ṭāriq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5933,86,'Ath-Thariq',3,"النَّجْمُ الثَّاقِبُۙ", "(yaitu) bintang yang bersinar tajam,", "an-najmuṡ-ṡāqib");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5934,86,'Ath-Thariq',4,"اِنْ كُلُّ نَفْسٍ لَّمَّا عَلَيْهَا حَافِظٌۗ", "setiap orang pasti ada penjaganya.", "ing kullu nafsil lammā 'alaihā ḥāfiẓ");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5935,86,'Ath-Thariq',5,"فَلْيَنْظُرِ الْاِنْسَانُ مِمَّ خُلِقَ", "Maka hendaklah manusia memperhatikan dari apa dia diciptakan.", "falyanẓuril-insānu mimma khuliq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5936,86,'Ath-Thariq',6,"خُلِقَ مِنْ مَّاۤءٍ دَافِقٍۙ", "Dia diciptakan dari air (mani) yang terpancar,", "khuliqa mim mā`in dāfiq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5937,86,'Ath-Thariq',7,"يَّخْرُجُ مِنْۢ بَيْنِ الصُّلْبِ وَالتَّرَاۤىِٕبِۗ", "yang keluar dari antara tulang punggung (sulbi) dan tulang dada.", "yakhruju mim bainiṣ-ṣulbi wat-tarā`ib");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5938,86,'Ath-Thariq',8,"اِنَّهٗ عَلٰى رَجْعِهٖ لَقَادِرٌۗ", "Sungguh, Allah benar-benar kuasa untuk mengembalikannya (hidup setelah mati).", "innahụ 'alā raj'ihī laqādir");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5939,86,'Ath-Thariq',9,"يَوْمَ تُبْلَى السَّرَاۤىِٕرُۙ", "Pada hari ditampakkan segala rahasia,", "yauma tublas-sarā`ir");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5940,86,'Ath-Thariq',10,"فَمَا لَهٗ مِنْ قُوَّةٍ وَّلَا نَاصِرٍۗ", "maka manusia tidak lagi mempunyai suatu kekuatan dan tidak (pula) ada penolong.", "fa mā lahụ ming quwwatiw wa lā nāṣir");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5941,86,'Ath-Thariq',11,"وَالسَّمَاۤءِ ذَاتِ الرَّجْعِۙ", "Demi langit yang mengandung hujan,", "was-samā`i żātir-raj'");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5942,86,'Ath-Thariq',12,"وَالْاَرْضِ ذَاتِ الصَّدْعِۙ", "dan bumi yang mempunyai tumbuh-tumbuhan,", "wal-arḍi żātiṣ-ṣad'");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5943,86,'Ath-Thariq',13,"اِنَّهٗ لَقَوْلٌ فَصْلٌۙ", "sungguh, (Al-Qur'an) itu benar-benar firman pemisah (antara yang hak dan yang batil),", "innahụ laqaulun faṣl");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5944,86,'Ath-Thariq',14,"وَّمَا هُوَ بِالْهَزْلِۗ", "dan (Al-Qur'an) itu bukanlah sendagurauan.", "wa mā huwa bil-hazl");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5945,86,'Ath-Thariq',15,"اِنَّهُمْ يَكِيْدُوْنَ كَيْدًاۙ", "Sungguh, mereka (orang kafir) merencanakan tipu daya yang jahat.", "innahum yakīdụna kaidā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5946,86,'Ath-Thariq',16,"وَّاَكِيْدُ كَيْدًاۖ", "Dan Aku pun membuat rencana (tipu daya) yang jitu.", "wa akīdu kaidā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5947,86,'Ath-Thariq',17,"فَمَهِّلِ الْكٰفِرِيْنَ اَمْهِلْهُمْ رُوَيْدًا", "Karena itu berilah penangguhan kepada orang-orang kafir itu. Berilah mereka itu kesempatan untuk sementara waktu.", "fa mahhilil-kāfirīna am-hil-hum ruwaidā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5948,87,'Al-A`la',1,"سَبِّحِ اسْمَ رَبِّكَ الْاَعْلَىۙ", "Sucikanlah nama Tuhanmu Yang Mahatinggi,", "sabbiḥisma rabbikal-a'lā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5949,87,'Al-A`la',2,"الَّذِيْ خَلَقَ فَسَوّٰىۖ", "Yang menciptakan, lalu menyempurnakan (ciptaan-Nya).", "allażī khalaqa fa sawwā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5950,87,'Al-A`la',3,"وَالَّذِيْ قَدَّرَ فَهَدٰىۖ", "Yang menentukan kadar (masing-masing) dan memberi petunjuk,", "wallażī qaddara fa hadā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5951,87,'Al-A`la',4,"وَالَّذِيْٓ اَخْرَجَ الْمَرْعٰىۖ", "dan Yang menumbuhkan rerumputan,", "wallażī akhrajal-mar'ā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5952,87,'Al-A`la',5,"فَجَعَلَهٗ غُثَاۤءً اَحْوٰىۖ", "lalu dijadikan-Nya (rumput-rumput) itu kering kehitam-hitaman.", "fa ja'alahụ guṡā`an aḥwā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5953,87,'Al-A`la',6,"سَنُقْرِئُكَ فَلَا تَنْسٰىٓ ۖ", "Kami akan membacakan (Al-Qur'an) kepadamu (Muhammad) sehingga engkau tidak akan lupa,", "sanuqri`uka fa lā tansā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5954,87,'Al-A`la',7,"اِلَّا مَا شَاۤءَ اللّٰهُ ۗاِنَّهٗ يَعْلَمُ الْجَهْرَ وَمَا يَخْفٰىۗ", "kecuali jika Allah menghendaki. Sungguh, Dia mengetahui yang terang dan yang tersembunyi.", "illā mā syā`allāh, innahụ ya'lamul-jahra wa mā yakhfā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5955,87,'Al-A`la',8,"وَنُيَسِّرُكَ لِلْيُسْرٰىۖ", "Dan Kami akan memudahkan bagimu ke jalan kemudahan (mencapai kebahagiaan dunia dan akhirat),", "wa nuyassiruka lil-yusrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5956,87,'Al-A`la',9,"فَذَكِّرْ اِنْ نَّفَعَتِ الذِّكْرٰىۗ", "oleh sebab itu berikanlah peringatan, karena peringatan itu bermanfaat,", "fa żakkir in nafa'atiż-żikrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5957,87,'Al-A`la',10,"سَيَذَّكَّرُ مَنْ يَّخْشٰىۙ", "orang yang takut (kepada Allah) akan mendapat pelajaran,", "sayażżakkaru may yakhsyā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5958,87,'Al-A`la',11,"وَيَتَجَنَّبُهَا الْاَشْقَىۙ", "dan orang-orang yang celaka (kafir) akan menjauhinya,", "wa yatajannabuhal-asyqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5959,87,'Al-A`la',12,"الَّذِيْ يَصْلَى النَّارَ الْكُبْرٰىۚ", "(yaitu) orang yang akan memasuki api yang besar (neraka),", "allażī yaṣlan-nāral-kubrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5960,87,'Al-A`la',13,"ثُمَّ لَا يَمُوْتُ فِيْهَا وَلَا يَحْيٰىۗ", "selanjutnya dia di sana tidak mati dan tidak (pula) hidup.", "ṡumma lā yamụtu fīhā wa lā yaḥyā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5961,87,'Al-A`la',14,"قَدْ اَفْلَحَ مَنْ تَزَكّٰىۙ", "Sungguh beruntung orang yang menyucikan diri (dengan beriman),", "qad aflaḥa man tazakkā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5962,87,'Al-A`la',15,"وَذَكَرَ اسْمَ رَبِّهٖ فَصَلّٰىۗ", "dan mengingat nama Tuhannya, lalu dia salat.", "wa żakarasma rabbihī fa ṣallā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5963,87,'Al-A`la',16,"بَلْ تُؤْثِرُوْنَ الْحَيٰوةَ الدُّنْيَاۖ", "Sedangkan kamu (orang-orang kafir) memilih kehidupan dunia,", "bal tu`ṡirụnal-ḥayātad-dun-yā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5964,87,'Al-A`la',17,"وَالْاٰخِرَةُ خَيْرٌ وَّاَبْقٰىۗ", "padahal kehidupan akhirat itu lebih baik dan lebih kekal.", "wal-ākhiratu khairuw wa abqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5965,87,'Al-A`la',18,"اِنَّ هٰذَا لَفِى الصُّحُفِ الْاُوْلٰىۙ", "Sesungguhnya ini terdapat dalam kitab-kitab yang dahulu,", "inna hāżā lafiṣ-ṣuḥufil-ụlā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5966,87,'Al-A`la',19,"صُحُفِ اِبْرٰهِيْمَ وَمُوْسٰى", "(yaitu) kitab-kitab Ibrahim dan Musa.", "ṣuḥufi ibrāhīma wa mụsā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5967,88,'Al-Ghasyiyah',1,"هَلْ اَتٰىكَ حَدِيْثُ الْغَاشِيَةِۗ", "Sudahkah sampai kepadamu berita tentang (hari Kiamat)?", "hal atāka ḥadīṡul-gāsyiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5968,88,'Al-Ghasyiyah',2,"وُجُوْهٌ يَّوْمَىِٕذٍ خَاشِعَةٌ ۙ", "Pada hari itu banyak wajah yang tertunduk terhina,", "wujụhuy yauma`iżin khāsyi'ah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5969,88,'Al-Ghasyiyah',3,"عَامِلَةٌ نَّاصِبَةٌ ۙ", "(karena) bekerja keras lagi kepayahan,", "'āmilatun nāṣibah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5970,88,'Al-Ghasyiyah',4,"تَصْلٰى نَارًا حَامِيَةً ۙ", "mereka memasuki api yang sangat panas (neraka),", "taṣlā nāran ḥāmiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5971,88,'Al-Ghasyiyah',5,"تُسْقٰى مِنْ عَيْنٍ اٰنِيَةٍ ۗ", "diberi minum dari sumber mata air yang sangat panas.", "tusqā min 'ainin āniyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5972,88,'Al-Ghasyiyah',6,"لَيْسَ لَهُمْ طَعَامٌ اِلَّا مِنْ ضَرِيْعٍۙ", "Tidak ada makanan bagi mereka selain dari pohon yang berduri,", "laisa lahum ṭa'āmun illā min ḍarī'");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5973,88,'Al-Ghasyiyah',7,"لَّا يُسْمِنُ وَلَا يُغْنِيْ مِنْ جُوْعٍۗ", "yang tidak menggemukkan dan tidak menghilangkan lapar.", "lā yusminu wa lā yugnī min jụ'");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5974,88,'Al-Ghasyiyah',8,"وُجُوْهٌ يَّوْمَىِٕذٍ نَّاعِمَةٌ ۙ", "Pada hari itu banyak (pula) wajah yang berseri-seri,", "wujụhuy yauma`iżin nā'imah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5975,88,'Al-Ghasyiyah',9,"لِّسَعْيِهَا رَاضِيَةٌ ۙ", "merasa senang karena usahanya (sendiri),", "lisa'yihā rāḍiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5976,88,'Al-Ghasyiyah',10,"فِيْ جَنَّةٍ عَالِيَةٍۙ", "(mereka) dalam surga yang tinggi,", "fī jannatin 'āliyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5977,88,'Al-Ghasyiyah',11,"لَّا تَسْمَعُ فِيْهَا لَاغِيَةً ۗ", "di sana (kamu) tidak mendengar perkataan yang tidak berguna.", "lā tasma'u fīhā lāgiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5978,88,'Al-Ghasyiyah',12,"فِيْهَا عَيْنٌ جَارِيَةٌ ۘ", "Di sana ada mata air yang mengalir.", "fīhā 'ainun jāriyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5979,88,'Al-Ghasyiyah',13,"فِيْهَا سُرُرٌ مَّرْفُوْعَةٌ ۙ", "Di sana ada dipan-dipan yang ditinggikan,", "fīhā sururum marfụ'ah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5980,88,'Al-Ghasyiyah',14,"وَّاَكْوَابٌ مَّوْضُوْعَةٌ ۙ", "dan gelas-gelas yang tersedia (di dekatnya),", "wa akwābum mauḍụ'ah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5981,88,'Al-Ghasyiyah',15,"وَّنَمَارِقُ مَصْفُوْفَةٌ ۙ", "dan bantal-bantal sandaran yang tersusun,", "wa namāriqu maṣfụfah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5982,88,'Al-Ghasyiyah',16,"وَّزَرَابِيُّ مَبْثُوْثَةٌ ۗ", "dan permadani-permadani yang terhampar.", "wa zarābiyyu mabṡụṡah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5983,88,'Al-Ghasyiyah',17,"اَفَلَا يَنْظُرُوْنَ اِلَى الْاِبِلِ كَيْفَ خُلِقَتْۗ", "Maka tidakkah mereka memperhatikan unta, bagaimana diciptakan?", "a fa lā yanẓurụna ilal-ibili kaifa khuliqat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5984,88,'Al-Ghasyiyah',18,"وَاِلَى السَّمَاۤءِ كَيْفَ رُفِعَتْۗ", "dan langit, bagaimana ditinggikan?", "wa ilas-samā`i kaifa rufi'at");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5985,88,'Al-Ghasyiyah',19,"وَاِلَى الْجِبَالِ كَيْفَ نُصِبَتْۗ", "Dan gunung-gunung bagaimana ditegakkan?", "wa ilal-jibāli kaifa nuṣibat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5986,88,'Al-Ghasyiyah',20,"وَاِلَى الْاَرْضِ كَيْفَ سُطِحَتْۗ", "Dan bumi bagaimana dihamparkan?", "wa ilal-arḍi kaifa suṭiḥat");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5987,88,'Al-Ghasyiyah',21,"فَذَكِّرْۗ اِنَّمَآ اَنْتَ مُذَكِّرٌۙ", "Maka berilah peringatan, karena sesungguhnya engkau (Muhammad) hanyalah pemberi peringatan.", "fa żakkir, innamā anta mużakkir");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5988,88,'Al-Ghasyiyah',22,"لَّسْتَ عَلَيْهِمْ بِمُصَيْطِرٍۙ", "Engkau bukanlah orang yang berkuasa atas mereka,", "lasta 'alaihim bimuṣaiṭir");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5989,88,'Al-Ghasyiyah',23,"اِلَّا مَنْ تَوَلّٰى وَكَفَرَۙ", "kecuali (jika ada) orang yang berpaling dan kafir,", "illā man tawallā wa kafar");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5990,88,'Al-Ghasyiyah',24,"فَيُعَذِّبُهُ اللّٰهُ الْعَذَابَ الْاَكْبَرَۗ", "maka Allah akan mengazabnya dengan azab yang besar.", "fa yu'ażżibuhullāhul-'ażābal-akbar");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5991,88,'Al-Ghasyiyah',25,"اِنَّ اِلَيْنَآ اِيَابَهُمْ", "Sungguh, kepada Kamilah mereka kembali,", "inna ilainā iyābahum");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5992,88,'Al-Ghasyiyah',26,"ثُمَّ اِنَّ عَلَيْنَا حِسَابَهُمْ", "kemudian sesungguhnya (kewajiban) Kamilah membuat perhitungan atas mereka.", "ṡumma inna 'alainā ḥisābahum");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5993,89,'Al-Fajr',1,"وَالْفَجْرِۙ", "Demi fajar,", "wal-fajr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5994,89,'Al-Fajr',2,"وَلَيَالٍ عَشْرٍۙ", "demi malam yang sepuluh,", "wa layālin 'asyr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5995,89,'Al-Fajr',3,"وَّالشَّفْعِ وَالْوَتْرِۙ", "demi yang genap dan yang ganjil,", "wasy-syaf'i wal-watr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5996,89,'Al-Fajr',4,"وَالَّيْلِ اِذَا يَسْرِۚ", "demi malam apabila berlalu.", "wal-laili iżā yasr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5997,89,'Al-Fajr',5,"هَلْ فِيْ ذٰلِكَ قَسَمٌ لِّذِيْ حِجْرٍۗ", "Adakah pada yang demikian itu terdapat sumpah (yang dapat diterima) bagi orang-orang yang berakal?", "hal fī żālika qasamul liżī ḥijr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5998,89,'Al-Fajr',6,"اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍۖ", "Tidakkah engkau (Muhammad) memperhatikan bagaimana Tuhanmu berbuat terhadap (kaum) ‘Ad?", "a lam tara kaifa fa'ala rabbuka bi'ād");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (5999,89,'Al-Fajr',7,"اِرَمَ ذَاتِ الْعِمَادِۖ", "(yaitu) penduduk Iram (ibukota kaum ‘Ad) yang mempunyai bangunan-bangunan yang tinggi,", "irama żātil-'imād");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6000,89,'Al-Fajr',8,"الَّتِيْ لَمْ يُخْلَقْ مِثْلُهَا فِى الْبِلَادِۖ", "yang belum pernah dibangun (suatu kota) seperti itu di negeri-negeri lain,", "allatī lam yukhlaq miṡluhā fil-bilād");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6001,89,'Al-Fajr',9,"وَثَمُوْدَ الَّذِيْنَ جَابُوا الصَّخْرَ بِالْوَادِۖ", "dan (terhadap) kaum samud yang memotong batu-batu besar di lembah,", "wa ṡamụdallażīna jābuṣ-ṣakhra bil-wād");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6002,89,'Al-Fajr',10,"وَفِرْعَوْنَ ذِى الْاَوْتَادِۖ", "dan (terhadap) Fir‘aun yang mempunyai pasak-pasak (bangunan yang besar),", "wa fir'auna żil-autād");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6003,89,'Al-Fajr',11,"الَّذِيْنَ طَغَوْا فِى الْبِلَادِۖ", "yang berbuat sewenang-wenang dalam negeri,", "allażīna ṭagau fil-bilād");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6004,89,'Al-Fajr',12,"فَاَكْثَرُوْا فِيْهَا الْفَسَادَۖ", "lalu mereka banyak berbuat kerusakan dalam negeri itu,", "fa akṡarụ fīhal-fasād");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6005,89,'Al-Fajr',13,"فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍۖ", "karena itu Tuhanmu menimpakan cemeti azab kepada mereka,", "fa ṣabba 'alaihim rabbuka sauṭa 'ażāb");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6006,89,'Al-Fajr',14,"اِنَّ رَبَّكَ لَبِالْمِرْصَادِۗ", "sungguh, Tuhanmu benar-benar mengawasi.", "inna rabbaka labil-mirṣād");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6007,89,'Al-Fajr',15,"فَاَمَّا الْاِنْسَانُ اِذَا مَا ابْتَلٰىهُ رَبُّهٗ فَاَكْرَمَهٗ وَنَعَّمَهٗۙ فَيَقُوْلُ رَبِّيْٓ اَكْرَمَنِۗ", "Maka adapun manusia, apabila Tuhan mengujinya lalu memuliakannya dan memberinya kesenangan, maka dia berkata, “Tuhanku telah memuliakanku.”", "fa ammal-insānu iżā mabtalāhu rabbuhụ fa akramahụ wa na''amahụ fa yaqụlu rabbī akraman");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6008,89,'Al-Fajr',16,"وَاَمَّآ اِذَا مَا ابْتَلٰىهُ فَقَدَرَ عَلَيْهِ رِزْقَهٗ ەۙ فَيَقُوْلُ رَبِّيْٓ اَهَانَنِۚ", "Namun apabila Tuhan mengujinya lalu membatasi rezekinya, maka dia berkata, “Tuhanku telah menghinaku.”", "wa ammā iżā mabtalāhu fa qadara 'alaihi rizqahụ fa yaqụlu rabbī ahānan");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6009,89,'Al-Fajr',17,"كَلَّا بَلْ لَّا تُكْرِمُوْنَ الْيَتِيْمَۙ", "Sekali-kali tidak! Bahkan kamu tidak memuliakan anak yatim,", "kallā bal lā tukrimụnal-yatīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6010,89,'Al-Fajr',18,"وَلَا تَحٰۤضُّوْنَ عَلٰى طَعَامِ الْمِسْكِيْنِۙ", "dan kamu tidak saling mengajak memberi makan orang miskin,", "wa lā tahāḍḍụna 'alā ṭa'āmil-miskīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6011,89,'Al-Fajr',19,"وَتَأْكُلُوْنَ التُّرَاثَ اَكْلًا لَّمًّاۙ", "sedangkan kamu memakan harta warisan dengan cara mencampurbaurkan (yang halal dan yang haram),", "wa ta`kulụnat-turāṡa aklal lammā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6012,89,'Al-Fajr',20,"وَّتُحِبُّوْنَ الْمَالَ حُبًّا جَمًّاۗ", "dan kamu mencintai harta dengan kecintaan yang berlebihan.", "wa tuḥibbụnal-māla ḥubban jammā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6013,89,'Al-Fajr',21,"كَلَّآ اِذَا دُكَّتِ الْاَرْضُ دَكًّا دَكًّاۙ", "Sekali-kali tidak! Apabila bumi diguncangkan berturut-turut (berbenturan),", "kallā iżā dukkatil-arḍu dakkan dakkā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6014,89,'Al-Fajr',22,"وَّجَآءَ رَبُّكَ وَالْمَلَكُ صَفًّا صَفًّاۚ", "dan datanglah Tuhanmu; dan malaikat berbaris-baris,", "wa jā`a rabbuka wal-malaku ṣaffan ṣaffā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6015,89,'Al-Fajr',23,"وَجِايْۤءَ يَوْمَىِٕذٍۢ بِجَهَنَّمَۙ يَوْمَىِٕذٍ يَّتَذَكَّرُ الْاِنْسَانُ وَاَنّٰى لَهُ الذِّكْرٰىۗ", "dan pada hari itu diperlihatkan neraka Jahanam; pada hari itu sadarlah manusia, tetapi tidak berguna lagi baginya kesadaran itu.", "wa jī`a yauma`iżim bijahannama yauma`iżiy yatażakkarul-insānu wa annā lahuż-żikrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6016,89,'Al-Fajr',24,"يَقُوْلُ يٰلَيْتَنِيْ قَدَّمْتُ لِحَيَاتِيْۚ", "Dia berkata, “Alangkah baiknya sekiranya dahulu aku mengerjakan (kebajikan) untuk hidupku ini.”", "yaqụlu yā laitanī qaddamtu liḥayātī");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6017,89,'Al-Fajr',25,"فَيَوْمَىِٕذٍ لَّا يُعَذِّبُ عَذَابَهٗٓ اَحَدٌ ۙ", "Maka pada hari itu tidak ada seorang pun yang mengazab seperti azab-Nya (yang adil),", "fa yauma`iżil lā yu'ażżibu 'ażābahū aḥad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6018,89,'Al-Fajr',26,"وَّلَا يُوْثِقُ وَثَاقَهٗٓ اَحَدٌ ۗ", "dan tidak ada seorang pun yang mengikat seperti ikatan-Nya.", "wa lā yụṡiqu waṡāqahū aḥad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6019,89,'Al-Fajr',27,"يٰٓاَيَّتُهَا النَّفْسُ الْمُطْمَىِٕنَّةُۙ", "Wahai jiwa yang tenang!", "yā ayyatuhan-nafsul-muṭma`innah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6020,89,'Al-Fajr',28,"ارْجِعِيْٓ اِلٰى رَبِّكِ رَاضِيَةً مَّرْضِيَّةً ۚ", "Kembalilah kepada Tuhanmu dengan hati yang rida dan diridai-Nya.", "irji'ī ilā rabbiki rāḍiyatam marḍiyyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6021,89,'Al-Fajr',29,"فَادْخُلِيْ فِيْ عِبٰدِيْۙ", "Maka masuklah ke dalam golongan hamba-hamba-Ku,", "fadkhulī fī 'ibādī");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6022,89,'Al-Fajr',30,"وَادْخُلِيْ جَنَّتِيْࣖ", "dan masuklah ke dalam surga-Ku.", "wadkhulī jannatī");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6023,90,'Al-Balad',1,"لَآ اُقْسِمُ بِهٰذَا الْبَلَدِۙ", "Aku bersumpah dengan negeri ini (Mekah),", "lā uqsimu bihāżal-balad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6024,90,'Al-Balad',2,"وَاَنْتَ حِلٌّۢ بِهٰذَا الْبَلَدِۙ", "dan engkau (Muhammad), bertempat di negeri (Mekah) ini,", "wa anta ḥillum bihāżal-balad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6025,90,'Al-Balad',3,"وَوَالِدٍ وَّمَا وَلَدَۙ", "dan demi (pertalian) bapak dan anaknya.", "wa wālidiw wa mā walad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6026,90,'Al-Balad',4,"لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْ كَبَدٍۗ", "Sungguh, Kami telah menciptakan manusia berada dalam susah payah.", "laqad khalaqnal-insāna fī kabad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6027,90,'Al-Balad',5,"اَيَحْسَبُ اَنْ لَّنْ يَّقْدِرَ عَلَيْهِ اَحَدٌ ۘ", "Apakah dia (manusia) itu mengira bahwa tidak ada sesuatu pun yang berkuasa atasnya?", "a yaḥsabu al lay yaqdira 'alaihi aḥad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6028,90,'Al-Balad',6,"يَقُوْلُ اَهْلَكْتُ مَالًا لُّبَدًاۗ", "Dia mengatakan, “Aku telah menghabiskan harta yang banyak.”", "yaqụlu ahlaktu mālal lubadā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6029,90,'Al-Balad',7,"اَيَحْسَبُ اَنْ لَّمْ يَرَهٗٓ اَحَدٌۗ", "Apakah dia mengira bahwa tidak ada sesuatu pun yang melihatnya?", "a yaḥsabu al lam yarahū aḥad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6030,90,'Al-Balad',8,"اَلَمْ نَجْعَلْ لَّهٗ عَيْنَيْنِۙ", "Bukankah Kami telah menjadikan untuknya sepasang mata,", "a lam naj'al lahụ 'ainaīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6031,90,'Al-Balad',9,"وَلِسَانًا وَّشَفَتَيْنِۙ", "dan lidah dan sepasang bibir?", "wa lisānaw wa syafataīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6032,90,'Al-Balad',10,"وَهَدَيْنٰهُ النَّجْدَيْنِۙ", "Dan Kami telah menunjukkan kepadanya dua jalan (kebajikan dan kejahatan),", "wa hadaināhun-najdaīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6033,90,'Al-Balad',11,"فَلَا اقْتَحَمَ الْعَقَبَةَ ۖ", "tetapi dia tidak menempuh jalan yang mendaki dan sukar?", "fa laqtaḥamal-'aqabah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6034,90,'Al-Balad',12,"وَمَآ اَدْرٰىكَ مَا الْعَقَبَةُ ۗ", "Dan tahukah kamu apakah jalan yang mendaki dan sukar itu?", "wa mā adrāka mal-'aqabah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6035,90,'Al-Balad',13,"فَكُّ رَقَبَةٍۙ", "(yaitu) melepaskan perbudakan (hamba sahaya),", "fakku raqabah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6036,90,'Al-Balad',14,"اَوْ اِطْعَامٌ فِيْ يَوْمٍ ذِيْ مَسْغَبَةٍۙ", "atau memberi makan pada hari terjadi kelaparan,", "au iṭ'āmun fī yaumin żī masgabah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6037,90,'Al-Balad',15,"يَّتِيْمًا ذَا مَقْرَبَةٍۙ", "(kepada) anak yatim yang ada hubungan kerabat,", "yatīman żā maqrabah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6038,90,'Al-Balad',16,"اَوْ مِسْكِيْنًا ذَا مَتْرَبَةٍۗ", "atau orang miskin yang sangat fakir.", "au miskīnan żā matrabah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6039,90,'Al-Balad',17,"ثُمَّ كَانَ مِنَ الَّذِيْنَ اٰمَنُوْا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِۗ", "Kemudian dia termasuk orang-orang yang beriman dan saling berpesan untuk bersabar dan saling berpesan untuk berkasih sayang.", "ṡumma kāna minallażīna āmanụ wa tawāṣau biṣ-ṣabri wa tawāṣau bil-mar-ḥamah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6040,90,'Al-Balad',18,"اُولٰۤىِٕكَ اَصْحٰبُ الْمَيْمَنَةِۗ", "Mereka (orang-orang yang beriman dan saling berpesan itu) adalah golongan kanan.", "ulā`ika aṣ-ḥābul-maimanah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6041,90,'Al-Balad',19,"وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِنَا هُمْ اَصْحٰبُ الْمَشْئَمَةِۗ", "Dan orang-orang yang kafir kepada ayat-ayat Kami, mereka itu adalah golongan kiri.", "wallażīna kafarụ bi`āyātinā hum aṣ-ḥābul-masy`amah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6042,90,'Al-Balad',20,"عَلَيْهِمْ نَارٌ مُّؤْصَدَةٌ", "Mereka berada dalam neraka yang ditutup rapat.", "'alaihim nārum mu`ṣadah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6043,91,'Asy-Syams',1,"وَالشَّمْسِ وَضُحٰىهَاۖ", "Demi matahari dan sinarnya pada pagi hari,", "wasy-syamsi wa ḍuḥāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6044,91,'Asy-Syams',2,"وَالْقَمَرِ اِذَا تَلٰىهَاۖ", "demi bulan apabila mengiringinya,", "wal-qamari iżā talāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6045,91,'Asy-Syams',3,"وَالنَّهَارِ اِذَا جَلّٰىهَاۖ", "demi siang apabila menampakkannya,", "wan-nahāri iżā jallāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6046,91,'Asy-Syams',4,"وَالَّيْلِ اِذَا يَغْشٰىهَاۖ", "demi malam apabila menutupinya (gelap gulita),", "wal-laili iżā yagsyāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6047,91,'Asy-Syams',5,"وَالسَّمَاۤءِ وَمَا بَنٰىهَاۖ", "demi langit serta pembinaannya (yang menakjubkan),", "was-samā`i wa mā banāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6048,91,'Asy-Syams',6,"وَالْاَرْضِ وَمَا طَحٰىهَاۖ", "demi bumi serta penghamparannya,", "wal-arḍi wa mā ṭaḥāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6049,91,'Asy-Syams',7,"وَنَفْسٍ وَّمَا سَوّٰىهَاۖ", "demi jiwa serta penyempurnaan (ciptaan)nya,", "wa nafsiw wa mā sawwāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6050,91,'Asy-Syams',8,"فَاَلْهَمَهَا فُجُوْرَهَا وَتَقْوٰىهَاۖ", "maka Dia mengilhamkan kepadanya (jalan) kejahatan dan ketakwaannya,", "fa al-hamahā fujụrahā wa taqwāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6051,91,'Asy-Syams',9,"قَدْ اَفْلَحَ مَنْ زَكّٰىهَاۖ", "sungguh beruntung orang yang menyucikannya (jiwa itu),", "qad aflaḥa man zakkāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6052,91,'Asy-Syams',10,"وَقَدْ خَابَ مَنْ دَسّٰىهَاۗ", "dan sungguh rugi orang yang mengotorinya.", "wa qad khāba man dassāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6053,91,'Asy-Syams',11,"كَذَّبَتْ ثَمُوْدُ بِطَغْوٰىهَآ ۖ", "(Kaum) samud telah mendustakan (rasulnya) karena mereka melampaui batas (zalim),", "każżabaṡ ṡamụdu biṭagwāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6054,91,'Asy-Syams',12,"اِذِ انْۢبَعَثَ اَشْقٰىهَاۖ", "ketika bangkit orang yang paling celaka di antara mereka,", "iżimba'aṡa asyqāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6055,91,'Asy-Syams',13,"فَقَالَ لَهُمْ رَسُوْلُ اللّٰهِ نَاقَةَ اللّٰهِ وَسُقْيٰهَاۗ", "lalu Rasul Allah (Saleh) berkata kepada mereka, “(Biarkanlah) unta betina dari Allah ini dengan minumannya.”", "fa qāla lahum rasụlullāhi nāqatallāhi wa suqyāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6056,91,'Asy-Syams',14,"فَكَذَّبُوْهُ فَعَقَرُوْهَاۖ فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْۢبِهِمْ فَسَوّٰىهَاۖ", "Namun mereka mendustakannya dan menyembelihnya, karena itu Tuhan membinasakan mereka karena dosanya, lalu diratakan-Nya (dengan tanah),", "fa każżabụhu fa 'aqarụhā fa damdama 'alaihim rabbuhum biżambihim fa sawwāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6057,91,'Asy-Syams',15,"وَلَا يَخَافُ عُقْبٰهَا", "dan Dia tidak takut terhadap akibatnya.", "wa lā yakhāfu 'uqbāhā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6058,92,'Al-Lail',1,"وَالَّيْلِ اِذَا يَغْشٰىۙ", "Demi malam apabila menutupi (cahaya siang),", "wal-laili iżā yagsyā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6059,92,'Al-Lail',2,"وَالنَّهَارِ اِذَا تَجَلّٰىۙ", "demi siang apabila terang benderang,", "wan-nahāri iżā tajallā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6060,92,'Al-Lail',3,"وَمَا خَلَقَ الذَّكَرَ وَالْاُنْثٰىٓ ۙ", "demi penciptaan laki-laki dan perempuan,", "wa mā khalaqaż-żakara wal-unṡā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6061,92,'Al-Lail',4,"اِنَّ سَعْيَكُمْ لَشَتّٰىۗ", "sungguh, usahamu memang beraneka macam.", "inna sa'yakum lasyattā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6062,92,'Al-Lail',5,"فَاَمَّا مَنْ اَعْطٰى وَاتَّقٰىۙ", "Maka barangsiapa memberikan (hartanya di jalan Allah) dan bertakwa,", "fa ammā man a'ṭā wattaqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6063,92,'Al-Lail',6,"وَصَدَّقَ بِالْحُسْنٰىۙ", "dan membenarkan (adanya pahala) yang terbaik (surga),", "wa ṣaddaqa bil-ḥusnā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6064,92,'Al-Lail',7,"فَسَنُيَسِّرُهٗ لِلْيُسْرٰىۗ", "maka akan Kami mudahkan baginya jalan menuju kemudahan (kebahagiaan),", "fa sanuyassiruhụ lil-yusrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6065,92,'Al-Lail',8,"وَاَمَّا مَنْۢ بَخِلَ وَاسْتَغْنٰىۙ", "dan adapun orang yang kikir dan merasa dirinya cukup (tidak perlu pertolongan Allah),", "wa ammā mam bakhila wastagnā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6066,92,'Al-Lail',9,"وَكَذَّبَ بِالْحُسْنٰىۙ", "serta mendustakan (pahala) yang terbaik,", "wa każżaba bil-ḥusnā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6067,92,'Al-Lail',10,"فَسَنُيَسِّرُهٗ لِلْعُسْرٰىۗ", "maka akan Kami mudahkan baginya jalan menuju kesukaran (kesengsaraan),", "fa sanuyassiruhụ lil-'usrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6068,92,'Al-Lail',11,"وَمَا يُغْنِيْ عَنْهُ مَالُهٗٓ اِذَا تَرَدّٰىٓۙ", "dan hartanya tidak bermanfaat baginya apabila dia telah binasa.", "wa mā yugnī 'an-hu māluhū iżā taraddā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6069,92,'Al-Lail',12,"اِنَّ عَلَيْنَا لَلْهُدٰىۖ", "Sesungguhnya Kamilah yang memberi petunjuk,", "inna 'alainā lal-hudā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6070,92,'Al-Lail',13,"وَاِنَّ لَنَا لَلْاٰخِرَةَ وَالْاُوْلٰىۗ", "dan sesungguhnya milik Kamilah akhirat dan dunia itu.", "wa inna lanā lal-ākhirata wal-ụlā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6071,92,'Al-Lail',14,"فَاَنْذَرْتُكُمْ نَارًا تَلَظّٰىۚ", "Maka Aku memperingatkan kamu dengan neraka yang menyala-nyala,", "fa anżartukum nāran talaẓẓā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6072,92,'Al-Lail',15,"لَا يَصْلٰىهَآ اِلَّا الْاَشْقَىۙ", "yang hanya dimasuki oleh orang yang paling celaka,", "lā yaṣlāhā illal-asyqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6073,92,'Al-Lail',16,"الَّذِيْ كَذَّبَ وَتَوَلّٰىۗ", "yang mendustakan (kebenaran) dan berpaling (dari iman).", "allażī każżaba wa tawallā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6074,92,'Al-Lail',17,"وَسَيُجَنَّبُهَا الْاَتْقَىۙ", "Dan akan dijauhkan darinya (neraka) orang yang paling bertakwa,", "wa sayujannabuhal-atqā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6075,92,'Al-Lail',18,"الَّذِيْ يُؤْتِيْ مَالَهٗ يَتَزَكّٰىۚ", "yang menginfakkan hartanya (di jalan Allah) untuk membersihkan (dirinya),", "allażī yu`tī mālahụ yatazakkā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6076,92,'Al-Lail',19,"وَمَا لِاَحَدٍ عِنْدَهٗ مِنْ نِّعْمَةٍ تُجْزٰىٓۙ", "dan tidak ada seorang pun memberikan suatu nikmat padanya yang harus dibalasnya,", "wa mā li`aḥadin 'indahụ min ni'matin tujzā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6077,92,'Al-Lail',20,"اِلَّا ابْتِغَاۤءَ وَجْهِ رَبِّهِ الْاَعْلٰىۚ", "tetapi (dia memberikan itu semata-mata) karena mencari keridaan Tuhannya Yang Mahatinggi.", "illabtigā`a waj-hi rabbihil-a'lā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6078,92,'Al-Lail',21,"وَلَسَوْفَ يَرْضٰى", "Dan niscaya kelak dia akan mendapat kesenangan (yang sempurna).", "wa lasaufa yarḍā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6079,93,'Ad-Dhuha',1,"وَالضُّحٰىۙ", "Demi waktu duha (ketika matahari naik sepenggalah),", "waḍ-ḍuḥā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6080,93,'Ad-Dhuha',2,"وَالَّيْلِ اِذَا سَجٰىۙ", "dan demi malam apabila telah sunyi,", "wal-laili iżā sajā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6081,93,'Ad-Dhuha',3,"مَا وَدَّعَكَ رَبُّكَ وَمَا قَلٰىۗ", "Tuhanmu tidak meninggalkan engkau (Muhammad) dan tidak (pula) membencimu,", "mā wadda'aka rabbuka wa mā qalā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6082,93,'Ad-Dhuha',4,"وَلَلْاٰخِرَةُ خَيْرٌ لَّكَ مِنَ الْاُوْلٰىۗ", "dan sungguh, yang kemudian itu lebih baik bagimu dari yang permulaan.", "wa lal-ākhiratu khairul laka minal-ụlā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6083,93,'Ad-Dhuha',5,"وَلَسَوْفَ يُعْطِيْكَ رَبُّكَ فَتَرْضٰىۗ", "Dan sungguh, kelak Tuhanmu pasti memberikan karunia-Nya kepadamu, sehingga engkau menjadi puas.", "wa lasaufa yu'ṭīka rabbuka fa tarḍā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6084,93,'Ad-Dhuha',6,"اَلَمْ يَجِدْكَ يَتِيْمًا فَاٰوٰىۖ", "Bukankah Dia mendapatimu sebagai seorang yatim, lalu Dia melindungi(mu),", "a lam yajidka yatīman fa āwā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6085,93,'Ad-Dhuha',7,"وَوَجَدَكَ ضَاۤلًّا فَهَدٰىۖ", "dan Dia mendapatimu sebagai seorang yang bingung, lalu Dia memberikan petunjuk,", "wa wajadaka ḍāllan fa hadā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6086,93,'Ad-Dhuha',8,"وَوَجَدَكَ عَاۤىِٕلًا فَاَغْنٰىۗ", "dan Dia mendapatimu sebagai seorang yang kekurangan, lalu Dia memberikan kecukupan.", "wa wajadaka 'ā`ilan fa agnā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6087,93,'Ad-Dhuha',9,"فَاَمَّا الْيَتِيْمَ فَلَا تَقْهَرْۗ", "Maka terhadap anak yatim janganlah engkau berlaku sewenang-wenang.", "fa ammal-yatīma fa lā taq-har");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6088,93,'Ad-Dhuha',10,"وَاَمَّا السَّاۤىِٕلَ فَلَا تَنْهَرْ", "Dan terhadap orang yang meminta-minta janganlah engkau menghardik(nya).", "wa ammas-sā`ila fa lā tan-har");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6089,93,'Ad-Dhuha',11,"وَاَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ", "Dan terhadap nikmat Tuhanmu hendaklah engkau nyatakan (dengan bersyukur).", "wa ammā bini'mati rabbika fa ḥaddiṡ");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6090,94,'Al-Insyirah',1,"اَلَمْ نَشْرَحْ لَكَ صَدْرَكَۙ", "Bukankah Kami telah melapangkan dadamu (Muhammad)?", "a lam nasyraḥ laka ṣadrak");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6091,94,'Al-Insyirah',2,"وَوَضَعْنَا عَنْكَ وِزْرَكَۙ", "dan Kami pun telah menurunkan bebanmu darimu,", "wa waḍa'nā 'angka wizrak");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6092,94,'Al-Insyirah',3,"الَّذِيْٓ اَنْقَضَ ظَهْرَكَۙ", "yang memberatkan punggungmu,", "allażī angqaḍa ẓahrak");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6093,94,'Al-Insyirah',4,"وَرَفَعْنَا لَكَ ذِكْرَكَۗ", "dan Kami tinggikan sebutan (nama)mu bagimu.", "wa rafa'nā laka żikrak");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6094,94,'Al-Insyirah',5,"فَاِنَّ مَعَ الْعُسْرِ يُسْرًاۙ", "Maka sesungguhnya beserta kesulitan ada kemudahan,", "fa inna ma'al-'usri yusrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6095,94,'Al-Insyirah',6,"اِنَّ مَعَ الْعُسْرِ يُسْرًاۗ", "sesungguhnya beserta kesulitan itu ada kemudahan.", "inna ma'al-'usri yusrā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6096,94,'Al-Insyirah',7,"فَاِذَا فَرَغْتَ فَانْصَبْۙ", "Maka apabila engkau telah selesai (dari sesuatu urusan), tetaplah bekerja keras (untuk urusan yang lain),", "fa iżā faragta fanṣab");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6097,94,'Al-Insyirah',8,"وَاِلٰى رَبِّكَ فَارْغَبْ", "dan hanya kepada Tuhanmulah engkau berharap.", "wa ilā rabbika fargab");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6098,95,'At-Tin',1,"وَالتِّيْنِ وَالزَّيْتُوْنِۙ", "Demi (buah) Tin dan (buah) Zaitun,", "wat-tīni waz-zaitụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6099,95,'At-Tin',2,"وَطُوْرِ سِيْنِيْنَۙ", "demi gunung Sinai,", "wa ṭụri sīnīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6100,95,'At-Tin',3,"وَهٰذَا الْبَلَدِ الْاَمِيْنِۙ", "dan demi negeri (Mekah) yang aman ini.", "wa hāżal-baladil-amīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6101,95,'At-Tin',4,"لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْٓ اَحْسَنِ تَقْوِيْمٍۖ", "Sungguh, Kami telah menciptakan manusia dalam bentuk yang sebaik-baiknya,", "laqad khalaqnal-insāna fī aḥsani taqwīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6102,95,'At-Tin',5,"ثُمَّ رَدَدْنٰهُ اَسْفَلَ سَافِلِيْنَۙ", "kemudian Kami kembalikan dia ke tempat yang serendah-rendahnya,", "ṡumma radadnāhu asfala sāfilīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6103,95,'At-Tin',6,"اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍۗ", "kecuali orang-orang yang beriman dan mengerjakan kebajikan; maka mereka akan mendapat pahala yang tidak ada putus-putusnya.", "illallażīna āmanụ wa 'amiluṣ-ṣāliḥāti fa lahum ajrun gairu mamnụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6104,95,'At-Tin',7,"فَمَا يُكَذِّبُكَ بَعْدُ بِالدِّيْنِۗ", "Maka apa yang menyebabkan (mereka) mendustakanmu (tentang) hari pembalasan setelah (adanya keterangan-keterangan) itu?", "fa mā yukażżibuka ba'du bid-dīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6105,95,'At-Tin',8,"اَلَيْسَ اللّٰهُ بِاَحْكَمِ الْحٰكِمِيْنَ", "Bukankah Allah hakim yang paling adil?", "a laisallāhu bi`aḥkamil-ḥākimīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6106,96,'Al-`Alaq',1,"اِقْرَأْ بِاسْمِ رَبِّكَ الَّذِيْ خَلَقَۚ", "Bacalah dengan (menyebut) nama Tuhanmu yang menciptakan,", "iqra` bismi rabbikallażī khalaq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6107,96,'Al-`Alaq',2,"خَلَقَ الْاِنْسَانَ مِنْ عَلَقٍۚ", "Dia telah menciptakan manusia dari segumpal darah.", "khalaqal-insāna min 'alaq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6108,96,'Al-`Alaq',3,"اِقْرَأْ وَرَبُّكَ الْاَكْرَمُۙ", "Bacalah, dan Tuhanmulah Yang Mahamulia,", "iqra` wa rabbukal-akram");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6109,96,'Al-`Alaq',4,"الَّذِيْ عَلَّمَ بِالْقَلَمِۙ", "Yang mengajar (manusia) dengan pena.", "allażī 'allama bil-qalam");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6110,96,'Al-`Alaq',5,"عَلَّمَ الْاِنْسَانَ مَا لَمْ يَعْلَمْۗ", "Dia mengajarkan manusia apa yang tidak diketahuinya.", "'allamal-insāna mā lam ya'lam");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6111,96,'Al-`Alaq',6,"كَلَّآ اِنَّ الْاِنْسَانَ لَيَطْغٰىٓ ۙ", "Sekali-kali tidak! Sungguh, manusia itu benar-benar melampaui batas,", "kallā innal-insāna layaṭgā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6112,96,'Al-`Alaq',7,"اَنْ رَّاٰهُ اسْتَغْنٰىۗ", "apabila melihat dirinya serba cukup.", "ar ra`āhustagnā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6113,96,'Al-`Alaq',8,"اِنَّ اِلٰى رَبِّكَ الرُّجْعٰىۗ", "Sungguh, hanya kepada Tuhanmulah tempat kembali(mu).", "inna ilā rabbikar-ruj'ā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6114,96,'Al-`Alaq',9,"اَرَاَيْتَ الَّذِيْ يَنْهٰىۙ", "Bagaimana pendapatmu tentang orang yang melarang?", "a ra`aitallażī yan-hā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6115,96,'Al-`Alaq',10,"عَبْدًا اِذَا صَلّٰىۗ", "seorang hamba ketika dia melaksanakan salat,", "'abdan iżā ṣallā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6116,96,'Al-`Alaq',11,"اَرَاَيْتَ اِنْ كَانَ عَلَى الْهُدٰىٓۙ", "bagaimana pendapatmu jika dia (yang dilarang salat itu) berada di atas kebenaran (petunjuk),", "a ra`aita ing kāna 'alal-hudā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6117,96,'Al-`Alaq',12,"اَوْ اَمَرَ بِالتَّقْوٰىۗ", "atau dia menyuruh bertakwa (kepada Allah)?", "au amara bit-taqwā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6118,96,'Al-`Alaq',13,"اَرَاَيْتَ اِنْ كَذَّبَ وَتَوَلّٰىۗ", "Bagaimana pendapatmu jika dia (yang melarang) itu mendustakan dan berpaling?", "a ra`aita ing każżaba wa tawallā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6119,96,'Al-`Alaq',14,"اَلَمْ يَعْلَمْ بِاَنَّ اللّٰهَ يَرٰىۗ", "Tidakkah dia mengetahui bahwa sesungguhnya Allah melihat (segala perbuatannya)?", "a lam ya'lam bi`annallāha yarā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6120,96,'Al-`Alaq',15,"كَلَّا لَىِٕنْ لَّمْ يَنْتَهِ ەۙ لَنَسْفَعًاۢ بِالنَّاصِيَةِۙ", "Sekali-kali tidak! Sungguh, jika dia tidak berhenti (berbuat demikian) niscaya Kami tarik ubun-ubunnya, (ke dalam neraka),", "kallā la`il lam yantahi lanasfa'am bin-nāṣiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6121,96,'Al-`Alaq',16,"نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍۚ", "yaitu) ubun-ubun orang yang mendustakan dan durhaka.", "nāṣiyating kāżibatin khāṭi`ah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6122,96,'Al-`Alaq',17,"فَلْيَدْعُ نَادِيَهٗۙ", "Maka biarlah dia memanggil golongannya (untuk menolongnya),", "falyad'u nādiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6123,96,'Al-`Alaq',18,"سَنَدْعُ الزَّبَانِيَةَۙ", "Kelak Kami akan memanggil Malaikat Zabaniyah, (penyiksa orang-orang yang berdosa),", "sanad'uz-zabāniyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6124,96,'Al-`Alaq',19,"كَلَّاۗ لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ ۩", "sekali-kali tidak! Janganlah kamu patuh kepadanya; dan sujudlah serta dekatkanlah (dirimu kepada Allah).", "kallā, lā tuṭi'hu wasjud waqtarib");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6125,97,'Al-Qadr',1,"اِنَّآ اَنْزَلْنٰهُ فِيْ لَيْلَةِ الْقَدْرِ", "Sesungguhnya Kami telah menurunkannya (Al-Qur'an) pada malam qadar.", "innā anzalnāhu fī lailatil-qadr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6126,97,'Al-Qadr',2,"وَمَآ اَدْرٰىكَ مَا لَيْلَةُ الْقَدْرِۗ", "Dan tahukah kamu apakah malam kemuliaan itu?", "wa mā adrāka mā lailatul-qadr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6127,97,'Al-Qadr',3,"لَيْلَةُ الْقَدْرِ ەۙ خَيْرٌ مِّنْ اَلْفِ شَهْرٍۗ", "Malam kemuliaan itu lebih baik daripada seribu bulan.", "lailatul-qadri khairum min alfi syahr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6128,97,'Al-Qadr',4,"تَنَزَّلُ الْمَلٰۤىِٕكَةُ وَالرُّوْحُ فِيْهَا بِاِذْنِ رَبِّهِمْۚ مِنْ كُلِّ اَمْرٍۛ", "Pada malam itu turun para malaikat dan Ruh (Jibril) dengan izin Tuhannya untuk mengatur semua urusan.", "tanazzalul-malā`ikatu war-rụḥu fīhā bi`iżni rabbihim, ming kulli amr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6129,97,'Al-Qadr',5,"سَلٰمٌ ۛهِيَ حَتّٰى مَطْلَعِ الْفَجْرِ", "Sejahteralah (malam itu) sampai terbit fajar.", "salāmun hiya ḥattā maṭla'il-fajr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6130,98,'Al-Bayyinah',1,"لَمْ يَكُنِ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ مُنْفَكِّيْنَ حَتّٰى تَأْتِيَهُمُ الْبَيِّنَةُۙ", "Orang-orang yang kafir dari golongan Ahli Kitab dan orang-orang musyrik tidak akan meninggalkan (agama mereka) sampai datang kepada mereka bukti yang nyata,", "lam yakunillażīna kafarụ min ahlil-kitābi wal-musyrikīna munfakkīna ḥattā ta`tiyahumul-bayyinah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6131,98,'Al-Bayyinah',2,"رَسُوْلٌ مِّنَ اللّٰهِ يَتْلُوْا صُحُفًا مُّطَهَّرَةًۙ", "(yaitu) seorang Rasul dari Allah (Muhammad) yang membacakan lembaran-lembaran yang suci (Al-Qur'an),", "rasụlum minallāhi yatlụ ṣuḥufam muṭahharah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6132,98,'Al-Bayyinah',3,"فِيْهَا كُتُبٌ قَيِّمَةٌ ۗ", "di dalamnya terdapat (isi) kitab-kitab yang lurus (benar).", "fīhā kutubung qayyimah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6133,98,'Al-Bayyinah',4,"وَمَا تَفَرَّقَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنَةُ ۗ", "Dan tidaklah terpecah-belah orang-orang Ahli Kitab melainkan setelah datang kepada mereka bukti yang nyata.", "wa mā tafarraqallażīna ụtul-kitāba illā mim ba'di mā jā`at-humul-bayyinah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6134,98,'Al-Bayyinah',5,"وَمَآ اُمِرُوْٓا اِلَّا لِيَعْبُدُوا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۙ حُنَفَاۤءَ وَيُقِيْمُوا الصَّلٰوةَ وَيُؤْتُوا الزَّكٰوةَ وَذٰلِكَ دِيْنُ الْقَيِّمَةِۗ", "Padahal mereka hanya diperintah menyembah Allah dengan ikhlas menaati-Nya semata-mata karena (menjalankan) agama, dan juga agar melaksanakan salat dan menunaikan zakat; dan yang demikian itulah agama yang lurus (benar).", "wa mā umirū illā liya'budullāha mukhliṣīna lahud-dīna ḥunafā`a wa yuqīmuṣ-ṣalāta wa yu`tuz-zakāta wa żālika dīnul-qayyimah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6135,98,'Al-Bayyinah',6,"اِنَّ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ فِيْ نَارِ جَهَنَّمَ خٰلِدِيْنَ فِيْهَاۗ اُولٰۤىِٕكَ هُمْ شَرُّ الْبَرِيَّةِۗ", "Sungguh, orang-orang yang kafir dari golongan Ahli Kitab dan orang-orang musyrik (akan masuk) ke neraka Jahanam; mereka kekal di dalamnya selama-lamanya. Mereka itu adalah sejahat-jahat makhluk.", "innallażīna kafarụ min ahlil-kitābi wal-musyrikīna fī nāri jahannama khālidīna fīhā, ulā`ika hum syarrul-bariyyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6136,98,'Al-Bayyinah',7,"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اُولٰۤىِٕكَ هُمْ خَيْرُ الْبَرِيَّةِۗ", "Sungguh, orang-orang yang beriman dan mengerjakan kebajikan, mereka itu adalah sebaik-baik makhluk.", "innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti ulā`ika hum khairul-bariyyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6137,98,'Al-Bayyinah',8,"جَزَاۤؤُهُمْ عِنْدَ رَبِّهِمْ جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗرَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ ۗ ذٰلِكَ لِمَنْ خَشِيَ رَبَّهٗ", "Balasan mereka di sisi Tuhan mereka ialah surga ’Adn yang mengalir di bawahnya sungai-sungai; mereka kekal di dalamnya selama-lamanya. Allah rida terhadap mereka dan mereka pun rida kepada-Nya. Yang demikian itu adalah (balasan) bagi orang yang takut kepada Tuhannya.", "jazā`uhum 'inda rabbihim jannātu 'adnin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, raḍiyallāhu 'an-hum wa raḍụ 'an-h, żālika liman khasyiya rabbah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6138,99,'Al-Zalzalah',1,"اِذَا زُلْزِلَتِ الْاَرْضُ زِلْزَالَهَاۙ", "Apabila bumi diguncangkan dengan guncangan yang dahsyat,", "iżā zulzilatil-arḍu zilzālahā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6139,99,'Al-Zalzalah',2,"وَاَخْرَجَتِ الْاَرْضُ اَثْقَالَهَاۙ", "dan bumi telah mengeluarkan beban-beban berat (yang dikandung)nya,", "wa akhrajatil-arḍu aṡqālahā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6140,99,'Al-Zalzalah',3,"وَقَالَ الْاِنْسَانُ مَا لَهَاۚ", "Dan manusia bertanya, “Apa yang terjadi pada bumi ini?”", "wa qālal-insānu mā lahā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6141,99,'Al-Zalzalah',4,"يَوْمَىِٕذٍ تُحَدِّثُ اَخْبَارَهَاۙ", "Pada hari itu bumi menyampaikan beritanya,", "yauma`iżin tuḥaddiṡu akhbārahā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6142,99,'Al-Zalzalah',5,"بِاَنَّ رَبَّكَ اَوْحٰى لَهَاۗ", "karena sesungguhnya Tuhanmu telah memerintahkan (yang sedemikian itu) padanya.", "bi`anna rabbaka auḥā lahā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6143,99,'Al-Zalzalah',6,"يَوْمَىِٕذٍ يَّصْدُرُ النَّاسُ اَشْتَاتًا ەۙ لِّيُرَوْا اَعْمَالَهُمْۗ", "Pada hari itu manusia keluar dari kuburnya dalam keadaan berkelompok-kelompok, untuk diperlihatkan kepada mereka (balasan) semua perbuatannya.", "yauma`iżiy yaṣdurun-nāsu asytātal liyurau a'mālahum");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6144,99,'Al-Zalzalah',7,"فَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَّرَهٗۚ", "Maka barangsiapa mengerjakan kebaikan seberat zarrah, niscaya dia akan melihat (balasan)nya,", "fa may ya'mal miṡqāla żarratin khairay yarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6145,99,'Al-Zalzalah',8,"وَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَّرَهٗ", "dan barangsiapa mengerjakan kejahatan seberat zarrah, niscaya dia akan melihat (balasan)nya.", "wa may ya'mal miṡqāla żarratin syarray yarah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6146,100,'Al-`Adiyat',1,"وَالْعٰدِيٰتِ ضَبْحًاۙ", "Demi kuda perang yang berlari kencang terengah-engah,", "wal-'ādiyāti ḍab-ḥā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6147,100,'Al-`Adiyat',2,"فَالْمُوْرِيٰتِ قَدْحًاۙ", "dan kuda yang memercikkan bunga api (dengan pukulan kuku kakinya),", "fal-mụriyāti qad-ḥā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6148,100,'Al-`Adiyat',3,"فَالْمُغِيْرٰتِ صُبْحًاۙ", "dan kuda yang menyerang (dengan tiba-tiba) pada waktu pagi,", "fal-mugīrāti ṣub-ḥā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6149,100,'Al-`Adiyat',4,"فَاَثَرْنَ بِهٖ نَقْعًاۙ", "sehingga menerbangkan debu,", "fa aṡarna bihī naq'ā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6150,100,'Al-`Adiyat',5,"فَوَسَطْنَ بِهٖ جَمْعًاۙ", "lalu menyerbu ke tengah-tengah kumpulan musuh,", "fa wasaṭna bihī jam'ā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6151,100,'Al-`Adiyat',6,"اِنَّ الْاِنْسَانَ لِرَبِّهٖ لَكَنُوْدٌ ۚ", "sungguh, manusia itu sangat ingkar, (tidak bersyukur) kepada Tuhannya,", "innal-insāna lirabbihī lakanụd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6152,100,'Al-`Adiyat',7,"وَاِنَّهٗ عَلٰى ذٰلِكَ لَشَهِيْدٌۚ", "dan sesungguhnya dia (manusia) menyaksikan (mengakui) keingkarannya,", "wa innahụ 'alā żālika lasyahīd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6153,100,'Al-`Adiyat',8,"وَاِنَّهٗ لِحُبِّ الْخَيْرِ لَشَدِيْدٌ ۗ", "dan sesungguhnya cintanya kepada harta benar-benar berlebihan.", "wa innahụ liḥubbil-khairi lasyadīd");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6154,100,'Al-`Adiyat',9,"۞ اَفَلَا يَعْلَمُ اِذَا بُعْثِرَ مَا فِى الْقُبُوْرِۙ", "Maka tidakkah dia mengetahui apabila apa yang di dalam kubur dikeluarkan,", "a fa lā ya'lamu iżā bu'ṡira mā fil-qubụr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6155,100,'Al-`Adiyat',10,"وَحُصِّلَ مَا فِى الصُّدُوْرِۙ", "dan apa yang tersimpan di dalam dada dilahirkan?", "wa huṣṣila mā fiṣ-ṣudụr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6156,100,'Al-`Adiyat',11,"اِنَّ رَبَّهُمْ بِهِمْ يَوْمَىِٕذٍ لَّخَبِيْرٌ", "sungguh, Tuhan mereka pada hari itu Mahateliti terhadap keadaan mereka.", "inna rabbahum bihim yauma`iżil lakhabīr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6157,101,'Al-Qari`ah',1,"اَلْقَارِعَةُۙ", "Hari Kiamat,", "al-qāri'ah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6158,101,'Al-Qari`ah',2,"مَا الْقَارِعَةُ ۚ", "Apakah hari Kiamat itu?", "mal-qāri'ah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6159,101,'Al-Qari`ah',3,"وَمَآ اَدْرٰىكَ مَا الْقَارِعَةُ ۗ", "Dan tahukah kamu apakah hari Kiamat itu?", "wa mā adrāka mal-qāri'ah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6160,101,'Al-Qari`ah',4,"يَوْمَ يَكُوْنُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوْثِۙ", "Pada hari itu manusia seperti laron yang beterbangan,", "yauma yakụnun-nāsu kal-farāsyil-mabṡụṡ");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6161,101,'Al-Qari`ah',5,"وَتَكُوْنُ الْجِبَالُ كَالْعِهْنِ الْمَنْفُوْشِۗ", "dan gunung-gunung seperti bulu yang dihambur-hamburkan.", "wa takụnul-jibālu kal-'ihnil-manfụsy");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6162,101,'Al-Qari`ah',6,"فَاَمَّا مَنْ ثَقُلَتْ مَوَازِينُهٗۙ", "Maka adapun orang yang berat timbangan (kebaikan)nya,", "fa ammā man ṡaqulat mawāzīnuh");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6163,101,'Al-Qari`ah',7,"فَهُوَ فِيْ عِيْشَةٍ رَّاضِيَةٍۗ", "maka dia berada dalam kehidupan yang memuaskan (senang).", "fa huwa fī 'īsyatir rāḍiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6164,101,'Al-Qari`ah',8,"وَاَمَّا مَنْ خَفَّتْ مَوَازِيْنُهٗۙ", "Dan adapun orang yang ringan timbangan (kebaikan)nya,", "wa ammā man khaffat mawāzīnuh");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6165,101,'Al-Qari`ah',9,"فَاُمُّهُ هَاوِيَةٌ ۗ", "maka tempat kembalinya adalah neraka Hawiyah.", "fa ummuhụ hāwiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6166,101,'Al-Qari`ah',10,"وَمَآ اَدْرٰىكَ مَا هِيَهْۗ", "Dan tahukah kamu apakah neraka Hawiyah itu?", "wa mā adrāka mā hiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6167,101,'Al-Qari`ah',11,"نَارٌ حَامِيَةٌ", "(Yaitu) api yang sangat panas.", "nārun ḥāmiyah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6168,102,'At-Takasur',1,"اَلْهٰىكُمُ التَّكَاثُرُۙ", "Bermegah-megahan telah melalaikan kamu,", "al-hākumut-takāṡur");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6169,102,'At-Takasur',2,"حَتّٰى زُرْتُمُ الْمَقَابِرَۗ", "sampai kamu masuk ke dalam kubur.", "ḥattā zurtumul-maqābir");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6170,102,'At-Takasur',3,"كَلَّا سَوْفَ تَعْلَمُوْنَۙ", "Sekali-kali tidak! Kelak kamu akan mengetahui (akibat perbuatanmu itu),", "kallā saufa ta'lamụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6171,102,'At-Takasur',4,"ثُمَّ كَلَّا سَوْفَ تَعْلَمُوْنَ", "kemudian sekali-kali tidak! Kelak kamu akan mengetahui.", "ṡumma kallā saufa ta'lamụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6172,102,'At-Takasur',5,"كَلَّا لَوْ تَعْلَمُوْنَ عِلْمَ الْيَقِيْنِۗ", "Sekali-kali tidak! Sekiranya kamu mengetahui dengan pasti,", "kallā lau ta'lamụna 'ilmal-yaqīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6173,102,'At-Takasur',6,"لَتَرَوُنَّ الْجَحِيْمَۙ", "niscaya kamu benar-benar akan melihat neraka Jahim,", "latarawunnal-jaḥīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6174,102,'At-Takasur',7,"ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَقِيْنِۙ", "kemudian kamu benar-benar akan melihatnya dengan mata kepala sendiri,", "ṡumma latarawunnahā 'ainal-yaqīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6175,102,'At-Takasur',8,"ثُمَّ لَتُسْـَٔلُنَّ يَوْمَىِٕذٍ عَنِ النَّعِيْمِ", "kemudian kamu benar-benar akan ditanya pada hari itu tentang kenikmatan (yang megah di dunia itu).", "ṡumma latus`alunna yauma`iżin 'anin-na'īm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6176,103,'Al-Ashr',1,"وَالْعَصْرِۙ", "Demi masa,", "wal-'aṣr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6177,103,'Al-Ashr',2,"اِنَّ الْاِنْسَانَ لَفِيْ خُسْرٍۙ", "sungguh, manusia berada dalam kerugian,", "innal-insāna lafī khusr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6178,103,'Al-Ashr',3,"اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَتَوَاصَوْا بِالْحَقِّ ەۙ وَتَوَاصَوْا بِالصَّبْرِ", "kecuali orang-orang yang beriman dan mengerjakan kebajikan serta saling menasihati untuk kebenaran dan saling menasihati untuk kesabaran.", "illallażīna āmanụ wa 'amiluṣ-ṣāliḥāti wa tawāṣau bil-ḥaqqi wa tawāṣau biṣ-ṣabr");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6179,104,'Al-Humazah',1,"وَيْلٌ لِّكُلِّ هُمَزَةٍ لُّمَزَةٍۙ", "Celakalah bagi setiap pengumpat dan pencela,", "wailul likulli humazatil lumazah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6180,104,'Al-Humazah',2," ۨالَّذِيْ جَمَعَ مَالًا وَّعَدَّدَهٗۙ", "yang mengumpulkan harta dan menghitung-hitungnya,", "allażī jama'a mālaw wa 'addadah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6181,104,'Al-Humazah',3,"يَحْسَبُ اَنَّ مَالَهٗٓ اَخْلَدَهٗۚ", "dia (manusia) mengira bahwa hartanya itu dapat mengekalkannya.", "yaḥsabu anna mālahū akhladah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6182,104,'Al-Humazah',4,"كَلَّا لَيُنْۢبَذَنَّ فِى الْحُطَمَةِۖ", "Sekali-kali tidak! Pasti dia akan dilemparkan ke dalam (neraka) Hutamah.", "kallā layumbażanna fil-ḥuṭamah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6183,104,'Al-Humazah',5,"وَمَآ اَدْرٰىكَ مَا الْحُطَمَةُ ۗ", "Dan tahukah kamu apakah (neraka) Hutamah itu?", "wa mā adrāka mal-ḥuṭamah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6184,104,'Al-Humazah',6,"نَارُ اللّٰهِ الْمُوْقَدَةُۙ", "(Yaitu) api (azab) Allah yang dinyalakan,", "nārullāhil-mụqadah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6185,104,'Al-Humazah',7,"الَّتِيْ تَطَّلِعُ عَلَى الْاَفْـِٕدَةِۗ", "yang (membakar) sampai ke hati.", "allatī taṭṭali'u 'alal-af`idah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6186,104,'Al-Humazah',8,"اِنَّهَا عَلَيْهِمْ مُّؤْصَدَةٌۙ", "Sungguh, api itu ditutup rapat atas (diri) mereka,", "innahā 'alaihim mu`ṣadah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6187,104,'Al-Humazah',9,"فِيْ عَمَدٍ مُّمَدَّدَةٍ", "(sedang mereka itu) diikat pada tiang-tiang yang panjang.", "fī 'amadim mumaddadah");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6188,105,'Al-Fil',1,"اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِاَصْحٰبِ الْفِيْلِۗ", "Tidakkah engkau (Muhammad) perhatikan bagaimana Tuhanmu telah bertindak terhadap pasukan bergajah?", "a lam tara kaifa fa'ala rabbuka bi`aṣ-ḥābil-fīl");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6189,105,'Al-Fil',2,"اَلَمْ يَجْعَلْ كَيْدَهُمْ فِيْ تَضْلِيْلٍۙ", "Bukankah Dia telah menjadikan tipu daya mereka itu sia-sia?", "a lam yaj'al kaidahum fī taḍlīl");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6190,105,'Al-Fil',3,"وَّاَرْسَلَ عَلَيْهِمْ طَيْرًا اَبَابِيْلَۙ", "dan Dia mengirimkan kepada mereka burung yang berbondong-bondong,", "wa arsala 'alaihim ṭairan abābīl");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6191,105,'Al-Fil',4,"تَرْمِيْهِمْ بِحِجَارَةٍ مِّنْ سِجِّيْلٍۙ", "yang melempari mereka dengan batu dari tanah liat yang dibakar,", "tarmīhim biḥijāratim min sijjīl");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6192,105,'Al-Fil',5,"فَجَعَلَهُمْ كَعَصْفٍ مَّأْكُوْلٍ", "sehingga mereka dijadikan-Nya seperti daun-daun yang dimakan (ulat).", "fa ja'alahum ka'aṣfim ma`kụl");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6193,106,'Quaraisy',1,"لِاِيْلٰفِ قُرَيْشٍۙ", "Karena kebiasaan orang-orang Quraisy,", "li`īlāfi quraīsy");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6194,106,'Quaraisy',2,"اٖلٰفِهِمْ رِحْلَةَ الشِّتَاۤءِ وَالصَّيْفِۚ", "(yaitu) kebiasaan mereka bepergian pada musim dingin dan musim panas.", "īlāfihim riḥlatasy-syitā`i waṣ-ṣaīf");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6195,106,'Quaraisy',3,"فَلْيَعْبُدُوْا رَبَّ هٰذَا الْبَيْتِۙ", "Maka hendaklah mereka menyembah Tuhan (pemilik) rumah ini (Ka‘bah),", "falya'budụ rabba hāżal-baīt");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6196,106,'Quaraisy',4,"الَّذِيْٓ اَطْعَمَهُمْ مِّنْ جُوْعٍ ەۙ وَّاٰمَنَهُمْ مِّنْ خَوْفٍ", "yang telah memberi makanan kepada mereka untuk menghilangkan lapar dan mengamankan mereka dari rasa ketakutan.", "allażī aṭ'amahum min jụ'iw wa āmanahum min khaụf");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6197,107,'Al-Ma`un',1,"اَرَءَيْتَ الَّذِيْ يُكَذِّبُ بِالدِّيْنِۗ", "Tahukah kamu (orang) yang mendustakan agama?", "a ra`aitallażī yukażżibu bid-dīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6198,107,'Al-Ma`un',2,"فَذٰلِكَ الَّذِيْ يَدُعُّ الْيَتِيْمَۙ", "Maka itulah orang yang menghardik anak yatim,", "fa żālikallażī yadu''ul-yatīm");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6199,107,'Al-Ma`un',3,"وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْكِيْنِۗ", "dan tidak mendorong memberi makan orang miskin.", "wa lā yaḥuḍḍu 'alā ṭa'āmil-miskīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6200,107,'Al-Ma`un',4,"فَوَيْلٌ لِّلْمُصَلِّيْنَۙ", "Maka celakalah orang yang salat,", "fa wailul lil-muṣallīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6201,107,'Al-Ma`un',5,"الَّذِيْنَ هُمْ عَنْ صَلَاتِهِمْ سَاهُوْنَۙ", "(yaitu) orang-orang yang lalai terhadap salatnya,", "allażīna hum 'an ṣalātihim sāhụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6202,107,'Al-Ma`un',6,"الَّذِيْنَ هُمْ يُرَاۤءُوْنَۙ", "yang berbuat ria,", "allażīna hum yurā`ụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6203,107,'Al-Ma`un',7,"وَيَمْنَعُوْنَ الْمَاعُوْنَ", "dan enggan (memberikan) bantuan.", "wa yamna'ụnal-mā'ụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6204,108,'Al-Kautsar',1,"اِنَّآ اَعْطَيْنٰكَ الْكَوْثَرَۗ", "Sungguh, Kami telah memberimu (Muhammad) nikmat yang banyak.", "innā a'ṭainākal-kauṡar");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6205,108,'Al-Kautsar',2,"فَصَلِّ لِرَبِّكَ وَانْحَرْۗ", "Maka laksanakanlah salat karena Tuhanmu, dan berkurbanlah (sebagai ibadah dan mendekatkan diri kepada Allah).", "fa ṣalli lirabbika wan-ḥar");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6206,108,'Al-Kautsar',3,"اِنَّ شَانِئَكَ هُوَ الْاَبْتَرُ", "Sungguh, orang-orang yang membencimu dialah yang terputus (dari rahmat Allah).", "inna syāni`aka huwal-abtar");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6207,109,'Al-Kafirun',1,"قُلْ يٰٓاَيُّهَا الْكٰفِرُوْنَۙ", "Katakanlah (Muhammad), “Wahai orang-orang kafir!", "qul yā ayyuhal-kāfirụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6208,109,'Al-Kafirun',2,"لَآ اَعْبُدُ مَا تَعْبُدُوْنَۙ", "aku tidak akan menyembah apa yang kamu sembah,", "lā a'budu mā ta'budụn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6209,109,'Al-Kafirun',3,"وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۚ", "dan kamu bukan penyembah apa yang aku sembah,", "wa lā antum 'ābidụna mā a'bud");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6210,109,'Al-Kafirun',4,"وَلَآ اَنَا۠ عَابِدٌ مَّا عَبَدْتُّمْۙ", "dan aku tidak pernah menjadi penyembah apa yang kamu sembah,", "wa lā ana 'ābidum mā 'abattum");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6211,109,'Al-Kafirun',5,"وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۗ", "dan kamu tidak pernah (pula) menjadi penyembah apa yang aku sembah.", "wa lā antum 'ābidụna mā a'bud");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6212,109,'Al-Kafirun',6,"لَكُمْ دِيْنُكُمْ وَلِيَ دِيْنِ", "Untukmu agamamu, dan untukku agamaku.”", "lakum dīnukum wa liya dīn");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6213,110,'An-Nashr',1,"اِذَا جَاۤءَ نَصْرُ اللّٰهِ وَالْفَتْحُۙ", "Apabila telah datang pertolongan Allah dan kemenangan,", "iżā jā`a naṣrullāhi wal-fat-ḥ");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6214,110,'An-Nashr',2,"وَرَاَيْتَ النَّاسَ يَدْخُلُوْنَ فِيْ دِيْنِ اللّٰهِ اَفْوَاجًاۙ", "dan engkau melihat manusia berbondong-bondong masuk agama Allah,", "wa ra`aitan-nāsa yadkhulụna fī dīnillāhi afwājā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6215,110,'An-Nashr',3,"فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُۗ اِنَّهٗ كَانَ تَوَّابًا", "maka bertasbihlah dalam dengan Tuhanmu dan mohonlah ampunan kepada-Nya. Sungguh, Dia Maha Penerima tobat.", "fa sabbiḥ biḥamdi rabbika wastagfir-h, innahụ kāna tawwābā");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6216,111,'Al-Lahab',1,"تَبَّتْ يَدَآ اَبِيْ لَهَبٍ وَّتَبَّۗ", "Binasalah kedua tangan Abu Lahab dan benar-benar binasa dia!", "tabbat yadā abī lahabiw wa tabb");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6217,111,'Al-Lahab',2,"مَآ اَغْنٰى عَنْهُ مَالُهٗ وَمَا كَسَبَۗ", "Tidaklah berguna baginya hartanya dan apa yang dia usahakan.", "mā agnā 'an-hu māluhụ wa mā kasab");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6218,111,'Al-Lahab',3,"سَيَصْلٰى نَارًا ذَاتَ لَهَبٍۙ", "Kelak dia akan masuk ke dalam api yang bergejolak (neraka).", "sayaṣlā nāran żāta lahab");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6219,111,'Al-Lahab',4,"وَّامْرَاَتُهٗ ۗحَمَّالَةَ الْحَطَبِۚ", "Dan (begitu pula) istrinya, pembawa kayu bakar (penyebar fitnah).", "wamra`atuh, ḥammālatal-ḥaṭab");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6220,111,'Al-Lahab',5,"فِيْ جِيْدِهَا حَبْلٌ مِّنْ مَّسَدٍ", "Di lehernya ada tali dari sabut yang dipintal.", "fī jīdihā ḥablum mim masad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6221,112,'Al-Ikhlas',1,"قُلْ هُوَ اللّٰهُ اَحَدٌۚ", "Katakanlah (Muhammad), “Dialah Allah, Yang Maha Esa.", "qul huwallāhu aḥad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6222,112,'Al-Ikhlas',2,"اَللّٰهُ الصَّمَدُۚ", "Allah tempat meminta segala sesuatu.", "allāhuṣ-ṣamad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6223,112,'Al-Ikhlas',3,"لَمْ يَلِدْ وَلَمْ يُوْلَدْۙ", "(Allah) tidak beranak dan tidak pula diperanakkan.", "lam yalid wa lam yụlad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6224,112,'Al-Ikhlas',4,"وَلَمْ يَكُنْ لَّهٗ كُفُوًا اَحَدٌ", "Dan tidak ada sesuatu yang setara dengan Dia.”", "wa lam yakul lahụ kufuwan aḥad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6225,113,'Al-Falaq',1,"قُلْ اَعُوْذُ بِرَبِّ الْفَلَقِۙ", "Katakanlah, “Aku berlindung kepada Tuhan yang menguasai subuh (fajar),", "qul a'ụżu birabbil-falaq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6226,113,'Al-Falaq',2,"مِنْ شَرِّ مَا خَلَقَۙ", "dari kejahatan (makhluk yang) Dia ciptakan,", "min syarri mā khalaq");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6227,113,'Al-Falaq',3,"وَمِنْ شَرِّ غَاسِقٍ اِذَا وَقَبَۙ", "dan dari kejahatan malam apabila telah gelap gulita,", "wa min syarri gāsiqin iżā waqab");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6228,113,'Al-Falaq',4,"وَمِنْ شَرِّ النَّفّٰثٰتِ فِى الْعُقَدِۙ", "dan dari kejahatan (perempuan-perempuan) penyihir yang meniup pada buhul-buhul (talinya),", "wa min syarrin-naffāṡāti fil-'uqad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6229,113,'Al-Falaq',5,"وَمِنْ شَرِّ حَاسِدٍ اِذَا حَسَدَ", "dan dari kejahatan orang yang dengki apabila dia dengki.”", "wa min syarri ḥāsidin iżā ḥasad");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6230,114,'An-Nas',1,"قُلْ اَعُوْذُ بِرَبِّ النَّاسِۙ", "Katakanlah, “Aku berlindung kepada Tuhannya manusia,", "qul a'ụżu birabbin-nās");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6231,114,'An-Nas',2,"مَلِكِ النَّاسِۙ", "Raja manusia,", "malikin-nās");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6232,114,'An-Nas',3,"اِلٰهِ النَّاسِۙ", "sembahan manusia,", "ilāhin-nās");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6233,114,'An-Nas',4,"مِنْ شَرِّ الْوَسْوَاسِ ەۙ الْخَنَّاسِۖ", "dari kejahatan (bisikan) setan yang bersembunyi,", "min syarril-waswāsil-khannās");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6234,114,'An-Nas',5,"الَّذِيْ يُوَسْوِسُ فِيْ صُدُوْرِ النَّاسِۙ", "yang membisikkan (kejahatan) ke dalam dada manusia,", "allażī yuwaswisu fī ṣudụrin-nās");
INSERT INTO quran_id (id, suraId, namaSura, verseID, ayahText, indoText, readText ) VALUES (6235,114,'An-Nas',6,"مِنَ الْجِنَّةِ وَالنَّاسِ", "dari (golongan) jin dan manusia.”", "minal-jinnati wan-nās");